const StudentDocuments = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    // Mock documents data
    setDocuments([
      { id: 1, name: 'Academic Transcript.pdf', type: 'Transcript', date: '2024-01-15', status: 'Verified' },
      { id: 2, name: 'Birth Certificate.pdf', type: 'Identification', date: '2024-01-10', status: 'Pending' },
      { id: 3, name: 'Recommendation Letter.pdf', type: 'Reference', date: '2024-01-08', status: 'Verified' }
    ]);
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploading(true);
      // Simulate upload
      setTimeout(() => {
        setDocuments(prev => [...prev, {
          id: prev.length + 1,
          name: file.name,
          type: 'Other',
          date: new Date().toISOString().split('T')[0],
          status: 'Pending'
        }]);
        setUploading(false);
      }, 2000);
    }
  };

  return (
    <>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main', mb: 4 }}>
        My Documents
      </Typography>

      {/* Upload Section */}
      <Card sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>Upload New Document</Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Button
            variant="contained"
            component="label"
            startIcon={<Upload />}
            disabled={uploading}
          >
            {uploading ? 'Uploading...' : 'Select File'}
            <input type="file" hidden onChange={handleFileUpload} />
          </Button>
          <Typography variant="body2" color="text.secondary">
            Upload transcripts, certificates, or other supporting documents
          </Typography>
        </Box>
      </Card>

      {/* Documents List */}
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>Uploaded Documents</Typography>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Document Name</TableCell>
                  <TableCell>Type</TableCell>
                  <TableCell>Upload Date</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {documents.map((doc) => (
                  <TableRow key={doc.id}>
                    <TableCell>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <Description sx={{ mr: 1, color: 'primary.main' }} />
                        {doc.name}
                      </Box>
                    </TableCell>
                    <TableCell>{doc.type}</TableCell>
                    <TableCell>{doc.date}</TableCell>
                    <TableCell>
                      <Chip 
                        label={doc.status} 
                        color={doc.status === 'Verified' ? 'success' : 'warning'}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      <IconButton size="small"><Visibility /></IconButton>
                      <IconButton size="small"><Download /></IconButton>
                      <IconButton size="small" color="error"><Delete /></IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>
    </>
  );
};

