const PostJob = () => {
  const [formData, setFormData] = useState({
    title: '',
    department: '',
    type: 'Full-time',
    location: '',
    salary: '',
    description: '',
    requirements: ''
  });

  return (
    <>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main', mb: 4 }}>
        Post New Job
      </Typography>

      <Card>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Job Title" value={formData.title} 
                onChange={(e) => setFormData({...formData, title: e.target.value})} />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Department" value={formData.department} 
                onChange={(e) => setFormData({...formData, department: e.target.value})} />
            </Grid>
            <Grid item xs={12} md={4}>
              <FormControl fullWidth>
                <InputLabel>Job Type</InputLabel>
                <Select value={formData.type} onChange={(e) => setFormData({...formData, type: e.target.value})}>
                  <MenuItem value="Full-time">Full-time</MenuItem>
                  <MenuItem value="Part-time">Part-time</MenuItem>
                  <MenuItem value="Contract">Contract</MenuItem>
                  <MenuItem value="Internship">Internship</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={4}>
              <TextField fullWidth label="Location" value={formData.location} 
                onChange={(e) => setFormData({...formData, location: e.target.value})} />
            </Grid>
            <Grid item xs={12} md={4}>
              <TextField fullWidth label="Salary Range" value={formData.salary} 
                onChange={(e) => setFormData({...formData, salary: e.target.value})} />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth multiline rows={4} label="Job Description" value={formData.description} 
                onChange={(e) => setFormData({...formData, description: e.target.value})} />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth multiline rows={4} label="Requirements" value={formData.requirements} 
                onChange={(e) => setFormData({...formData, requirements: e.target.value})} />
            </Grid>
          </Grid>
        </CardContent>
        <CardActions sx={{ p: 3, pt: 0 }}>
          <Button variant="contained" size="large">Post Job</Button>
          <Button variant="outlined">Save as Draft</Button>
        </CardActions>
      </Card>
    </>
  );
};

// ===== MAIN APP COMPONENT =====
const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  const LoginPage = ({ onLogin }) => {
    const handleDemoLogin = (role) => {
      const users = {
        student: { id: 1, name: 'Test Student', email: 'student@test.ls', role: 'student' },
        institution: { id: 2, name: 'Limkokwing University', email: 'institution@limkokwing.ls', role: 'institution' },
        company: { id: 3, name: 'Econet Telecom', email: 'company@econet.ls', role: 'company' },
        admin: { id: 4, name: 'System Admin', email: 'admin@lesotho.ls', role: 'admin' }
      };
      onLogin(users[role]);
    };

    return (
      <Box sx={{ 
        minHeight: '100vh', 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex',
        alignItems: 'center',
        py: 4
      }}>
        <Container maxWidth="md">
          <Paper elevation={24} sx={{ borderRadius: 4, overflow: 'hidden' }}>
            <Box sx={{ bgcolor: 'primary.main', color: 'white', p: 4, textAlign: 'center' }}>
              <School sx={{ fontSize: 64, mb: 2 }} />
              <Typography variant="h3" fontWeight="bold" gutterBottom>
                Lesotho Career Platform
              </Typography>
              <Typography variant="h6">
                Faculty of ICT - Limkokwing University
              </Typography>
            </Box>

            <Box sx={{ p: 4 }}>
              <Typography variant="h5" align="center" gutterBottom sx={{ mb: 4 }}>
                Demo Login - Select Your Role
              </Typography>

              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Card sx={{ p: 3, textAlign: 'center', cursor: 'pointer' }} 
                    onClick={() => handleDemoLogin('student')}>
                    <Person sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>Student</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Browse courses, apply online, and find jobs
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Card sx={{ p: 3, textAlign: 'center', cursor: 'pointer' }}
                    onClick={() => handleDemoLogin('institution')}>
                    <Business sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>Institution</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Manage courses and student applications
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Card sx={{ p: 3, textAlign: 'center', cursor: 'pointer' }}
                    onClick={() => handleDemoLogin('company')}>
                    <BusinessCenter sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>Company</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Post jobs and find qualified candidates
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Card sx={{ p: 3, textAlign: 'center', cursor: 'pointer' }}
                    onClick={() => handleDemoLogin('admin')}>
                    <AdminPanelSettings sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>Administrator</Typography>
                    <Typography variant="body2" color="text.secondary">
                      System management and analytics
                    </Typography>
                  </Card>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Container>
      </Box>
    );
  };

  const LandingPage = () => (
    <Box sx={{ flexGrow: 1 }}>
      {/* Hero Section */}
      <Box sx={{ 
        bgcolor: 'primary.main', 
        color: 'white', 
        py: 12,
        background: 'linear-gradient(135deg, #2E7D32 0%, #4CAF50 100%)'
      }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography variant="h2" fontWeight="bold" gutterBottom>
                Lesotho Career Guidance Platform
              </Typography>
              <Typography variant="h5" gutterBottom sx={{ mb: 4, opacity: 0.9 }}>
                Connecting students with educational institutions and career opportunities across Lesotho
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <Button 
                  variant="contained" 
                  size="large" 
