import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  useTheme,
  useMediaQuery,
  Menu,
  MenuItem,
  Avatar,
  Badge,
  Chip
} from '@mui/material';
import {
  Menu as MenuIcon,
  School,
  BusinessCenter,
  Dashboard,
  Person,
  Group,
  Notifications,
  AccountCircle,
  ExitToApp,
  Settings,
  Work
} from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar = ({ user, onLogout }) => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [notificationsAnchor, setNotificationsAnchor] = useState(null);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const location = useLocation();

  const menuItems = [
    { text: 'Home', path: '/', icon: <Dashboard /> },
    { 
      text: 'For Students', 
      path: '/student', 
      icon: <School />,
      children: [
        { text: 'Register', path: '/student/register' },
        { text: 'Login', path: '/student/login' },
        { text: 'Browse Courses', path: '/courses' },
        { text: 'Career Guidance', path: '/career-test' }
      ]
    },
    { 
      text: 'For Institutions', 
      path: '/institute', 
      icon: <Person />,
      children: [
        { text: 'Register', path: '/institute/register' },
        { text: 'Login', path: '/institute/login' }
      ]
    },
    { 
      text: 'For Companies', 
      path: '/company', 
      icon: <BusinessCenter />,
      children: [
        { text: 'Register', path: '/company/register' },
        { text: 'Login', path: '/company/login' },
        { text: 'Post Job', path: '/post-job' }
      ]
    }
  ];

  const notifications = [
    { id: 1, text: 'New job matches your profile', time: '5 min ago', read: false },
    { id: 2, text: 'Application status updated', time: '1 hour ago', read: false },
    { id: 3, text: 'New courses available', time: '2 hours ago', read: true }
  ];

  const unreadCount = notifications.filter(n => !n.read).length;

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleNotificationsOpen = (event) => {
    setNotificationsAnchor(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    setNotificationsAnchor(null);
  };

  const handleLogout = () => {
    handleMenuClose();
    onLogout();
    navigate('/');
  };

  const handleNavigation = (path) => {
    navigate(path);
    setMobileOpen(false);
    handleMenuClose();
  };

  const drawer = (
    <Box sx={{ width: 280 }} onClick={handleDrawerToggle}>
      <Toolbar sx={{ bgcolor: 'primary.main', color: 'white' }}>
        <School sx={{ mr: 2 }} />
        <Typography variant="h6" fontWeight="bold">
          CareerGuide
        </Typography>
      </Toolbar>
      <List>
        {menuItems.map((item) => (
          <ListItem 
            key={item.text} 
            button
            onClick={() => handleNavigation(item.path)}
            sx={{
              backgroundColor: location.pathname === item.path ? theme.palette.primary.main : 'transparent',
              color: location.pathname === item.path ? 'white' : 'inherit',
              '&:hover': {
                backgroundColor: theme.palette.primary.light,
                color: 'white'
              }
            }}
          >
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
        {!user && (
          <ListItem 
            button
            onClick={() => handleNavigation('/admin/login')}
            sx={{
              backgroundColor: location.pathname === '/admin/login' ? theme.palette.secondary.main : 'transparent',
              color: location.pathname === '/admin/login' ? 'white' : 'inherit',
              '&:hover': {
                backgroundColor: theme.palette.secondary.light,
                color: 'white'
              }
            }}
          >
            <Group sx={{ mr: 2 }} />
            <ListItemText primary="Admin Login" />
          </ListItem>
        )}
      </List>
    </Box>
  );

  const renderUserMenu = () => (
    <Menu
      anchorEl={anchorEl}
      open={Boolean(anchorEl)}
      onClose={handleMenuClose}
      PaperProps={{
        elevation: 3,
        sx: {
          mt: 1.5,
          minWidth: 200
        }
      }}
    >
      <MenuItem onClick={() => handleNavigation('/profile')}>
        <AccountCircle sx={{ mr: 2 }} />
        Profile
      </MenuItem>
      <MenuItem onClick={() => handleNavigation('/settings')}>
        <Settings sx={{ mr: 2 }} />
        Settings
      </MenuItem>
      <MenuItem onClick={handleLogout}>
        <ExitToApp sx={{ mr: 2 }} />
        Logout
      </MenuItem>
    </Menu>
  );

  const renderNotifications = () => (
    <Menu
      anchorEl={notificationsAnchor}
      open={Boolean(notificationsAnchor)}
      onClose={handleMenuClose}
      PaperProps={{
        elevation: 3,
        sx: {
          mt: 1.5,
          minWidth: 320,
          maxHeight: 400
        }
      }}
    >
      <Typography variant="h6" sx={{ p: 2, pb: 1 }}>
        Notifications
      </Typography>
      {notifications.map((notification) => (
        <MenuItem key={notification.id} onClick={handleMenuClose}>
          <Box>
            <Typography variant="body2" sx={{ fontWeight: notification.read ? 'normal' : 'bold' }}>
              {notification.text}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {notification.time}
            </Typography>
          </Box>
        </MenuItem>
      ))}
      <MenuItem onClick={handleMenuClose} sx={{ justifyContent: 'center' }}>
        <Typography variant="body2" color="primary">
          View All Notifications
        </Typography>
      </MenuItem>
    </Menu>
  );

  return (
    <>
      <AppBar 
        position="sticky" 
        elevation={2}
        sx={{ 
          background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`
        }}
      >
        <Toolbar>
          {/* Mobile Menu Button */}
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          
          {/* Logo */}
          <Box 
            sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              cursor: 'pointer',
              mr: 4 
            }}
            onClick={() => navigate('/')}
          >
            <School sx={{ mr: 1, fontSize: 28 }} />
            <Typography 
              variant="h6" 
              component="div" 
              sx={{ 
                fontWeight: 'bold',
                display: { xs: 'none', sm: 'block' }
              }}
            >
              CareerGuide
            </Typography>
          </Box>

          {/* Desktop Navigation */}
          {!isMobile && (
            <Box sx={{ display: 'flex', gap: 1, flexGrow: 1 }}>
              {menuItems.map((item) => (
                <Button
                  key={item.text}
                  color="inherit"
                  onClick={() => handleNavigation(item.path)}
                  startIcon={item.icon}
                  sx={{
                    fontWeight: location.pathname.startsWith(item.path) ? 'bold' : 'normal',
                    borderBottom: location.pathname.startsWith(item.path) ? '2px solid white' : 'none',
                    borderRadius: 0
                  }}
                >
                  {item.text}
                </Button>
              ))}
              {!user && (
                <Button
                  color="inherit"
                  onClick={() => handleNavigation('/admin/login')}
                  startIcon={<Group />}
                  sx={{
                    fontWeight: location.pathname === '/admin/login' ? 'bold' : 'normal',
                    borderBottom: location.pathname === '/admin/login' ? '2px solid white' : 'none',
                    borderRadius: 0,
                    ml: 'auto'
                  }}
                >
                  Admin
                </Button>
              )}
            </Box>
          )}

          {/* User Actions */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            {/* Notifications */}
            {user && (
              <IconButton color="inherit" onClick={handleNotificationsOpen}>
                <Badge badgeContent={unreadCount} color="error">
                  <Notifications />
                </Badge>
              </IconButton>
            )}

            {/* User Profile or Login Buttons */}
            {user ? (
              <>
                <Chip
                  icon={<Work />}
                  label={user.role}
                  variant="outlined"
                  sx={{ color: 'white', borderColor: 'white' }}
                />
                <IconButton color="inherit" onClick={handleProfileMenuOpen}>
                  <Avatar sx={{ width: 32, height: 32, bgcolor: 'white', color: 'primary.main' }}>
                    {user.name?.charAt(0).toUpperCase()}
                  </Avatar>
                </IconButton>
              </>
            ) : (
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button 
                  color="inherit" 
                  variant="outlined"
                  onClick={() => navigate('/student/login')}
                  sx={{ 
                    borderColor: 'white',
                    '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' }
                  }}
                >
                  Login
                </Button>
                <Button 
                  color="primary" 
                  variant="contained"
                  onClick={() => navigate('/student/register')}
                  sx={{ 
                    bgcolor: 'white',
                    color: 'primary.main',
                    '&:hover': { bgcolor: 'grey.100' }
                  }}
                >
                  Sign Up
                </Button>
              </Box>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      {/* Mobile Drawer */}
      <Drawer
        variant="temporary"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{ keepMounted: true }}
        sx={{
          display: { xs: 'block', md: 'none' },
          '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 280 },
        }}
      >
        {drawer}
      </Drawer>

      {/* Menus */}
      {renderUserMenu()}
      {renderNotifications()}
    </>
  );
};

export default Navbar;
