import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container,
  Typography,
  Button,
  Paper,
  TextField,
  Alert,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  AppBar,
  Toolbar,
  Menu,
  MenuItem,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Chip,
  Avatar,
  Badge,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Stepper,
  Step,
  StepLabel,
  FormControl,
  InputLabel,
  Select,
  Checkbox,
  ListItemButton,
  FormControlLabel,
  Tab,
  Tabs
} from '@mui/material';
import {
  School,
  Business,
  BusinessCenter,
  AdminPanelSettings,
  Login,
  HowToReg,
  Work,
  TrendingUp,
  Assignment,
  Notifications,
  Book,
  CheckCircle,
  Pending,
  Star,
  EmojiEvents,
  Menu as MenuIcon,
  Dashboard as DashboardIcon,
  People,
  CorporateFare,
  Description,
  Analytics,
  ExitToApp,
  Add,
  Edit,
  Delete,
  Upload,
  Download,
  Search,
  FilterList,
  DateRange,
  LocationOn,
  Email,
  Phone,
  Person,
  Security
} from '@mui/icons-material';

// ===== FIREBASE CONFIGURATION =====
const firebaseConfig = {
  apiKey: "your-api-key",
  authDomain: "your-project.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-project.appspot.com",
  messagingSenderId: "123456789",
  appId: "your-app-id"
};

// Initialize Firebase (you'll need to set this up with your actual config)
// const app = initializeApp(firebaseConfig);
// const db = getFirestore(app);
// const auth = getAuth(app);

// ===== THEME CONFIGURATION =====
const theme = createTheme({
  palette: {
    primary: {
      main: '#2E7D32',
      light: '#4CAF50',
      dark: '#1B5E20'
    },
    secondary: {
      main: '#FF6F00',
      light: '#FF9800',
      dark: '#E65100'
    },
    background: {
      default: '#f8f9fa',
      paper: '#ffffff'
    }
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h3: {
      fontWeight: 700,
    },
    h4: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    }
  },
  shape: {
    borderRadius: 12
  }
});

// ===== COMMON COMPONENTS =====

const Navbar = ({ user, onLogout }) => {
  const [anchorEl, setAnchorEl] = useState(null);
  const navigate = useNavigate();

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const getRoleIcon = () => {
    switch(user?.role) {
      case 'admin': return <AdminPanelSettings />;
      case 'institution': return <Business />;
      case 'company': return <BusinessCenter />;
      default: return <Person />;
    }
  };

  return (
    <AppBar position="static" elevation={2} sx={{ bgcolor: 'primary.main' }}>
      <Toolbar>
        <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1, cursor: 'pointer' }} onClick={() => navigate('/')}>
          <School sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ fontWeight: 'bold' }}>
            Lesotho Career Platform
          </Typography>
        </Box>

        {user ? (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Chip 
              icon={getRoleIcon()} 
              label={`${user.name} (${user.role})`} 
              variant="outlined" 
              sx={{ color: 'white', borderColor: 'white' }}
            />
            <IconButton color="inherit" onClick={handleMenu}>
              <MenuIcon />
            </IconButton>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleClose}
            >
              <MenuItem onClick={() => navigate('/dashboard')}>
                <ListItemIcon><DashboardIcon /></ListItemIcon>
                <ListItemText>Dashboard</ListItemText>
              </MenuItem>
              <MenuItem onClick={() => navigate('/profile')}>
                <ListItemIcon><Person /></ListItemIcon>
                <ListItemText>Profile</ListItemText>
              </MenuItem>
              <MenuItem onClick={onLogout}>
                <ListItemIcon><ExitToApp /></ListItemIcon>
                <ListItemText>Logout</ListItemText>
              </MenuItem>
            </Menu>
          </Box>
        ) : (
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button color="inherit" onClick={() => navigate('/login')}>Login</Button>
            <Button color="inherit" variant="outlined" onClick={() => navigate('/register')}>Register</Button>
          </Box>
        )}
      </Toolbar>
    </AppBar>
  );
};

const Sidebar = ({ user, currentPage, onPageChange }) => {
  const getMenuItems = () => {
    switch(user?.role) {
      case 'admin':
        return [
          { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
          { text: 'Institutions', icon: <Business />, path: '/institutions' },
          { text: 'Companies', icon: <BusinessCenter />, path: '/companies' },
          { text: 'Users', icon: <People />, path: '/users' },
          { text: 'Reports', icon: <Analytics />, path: '/reports' }
        ];
      case 'institution':
        return [
          { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
          { text: 'Courses', icon: <School />, path: '/courses' },
          { text: 'Applications', icon: <Assignment />, path: '/applications' },
          { text: 'Admissions', icon: <CheckCircle />, path: '/admissions' },
          { text: 'Profile', icon: <Business />, path: '/profile' }
        ];
      case 'company':
        return [
          { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
          { text: 'Post Job', icon: <Add />, path: '/post-job' },
          { text: 'Applicants', icon: <People />, path: '/applicants' },
          { text: 'Profile', icon: <BusinessCenter />, path: '/profile' }
        ];
      default:
        return [
          { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
          { text: 'Browse Courses', icon: <School />, path: '/courses' },
          { text: 'My Applications', icon: <Assignment />, path: '/applications' },
          { text: 'Jobs', icon: <Work />, path: '/jobs' },
          { text: 'Career Test', icon: <TrendingUp />, path: '/career-test' },
          { text: 'Profile', icon: <Person />, path: '/profile' }
        ];
    }
  };

  return (
    <Drawer variant="permanent" sx={{ width: 240, flexShrink: 0 }}>
      <Toolbar />
      <Box sx={{ overflow: 'auto', mt: 2 }}>
        <List>
          {getMenuItems().map((item) => (
            <ListItem key={item.text} disablePadding>
              <ListItemButton 
                selected={currentPage === item.path}
                onClick={() => onPageChange(item.path)}
              >
                <ListItemIcon>{item.icon}</ListItemIcon>
                <ListItemText primary={item.text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Box>
    </Drawer>
  );
};

const Footer = () => {
  return (
    <Box component="footer" sx={{ bgcolor: 'primary.main', color: 'white', py: 4, mt: 'auto' }}>
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          <Grid item xs={12} md={4}>
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
               Lesotho Career Platform
            </Typography>
            <Typography variant="body2">
              Connecting students with educational institutions and career opportunities across Lesotho.
            </Typography>
          </Grid>
          <Grid item xs={12} md={4}>
            <Typography variant="h6" gutterBottom>Contact Info</Typography>
            <Typography variant="body2">
              <Email sx={{ fontSize: 16, mr: 1 }} /> info@lesothocareer.ls
            </Typography>
            <Typography variant="body2">
              <Phone sx={{ fontSize: 16, mr: 1 }} /> +266 1234 5678
            </Typography>
          </Grid>
          <Grid item xs={12} md={4}>
            <Typography variant="h6" gutterBottom>Developed By</Typography>
            <Typography variant="body2">
              Faculty of Information & Communication Technology<br />
              Limkokwing University of Creative Technology<br />
              BSc. in Information Technology
            </Typography>
          </Grid>
        </Grid>
        <Box sx={{ borderTop: 1, borderColor: 'grey.800', mt: 3, pt: 2 }}>
