             2024 Lesotho Career Guidance and Employment Integration Platform. All rights reserved.
          </Typography>
        </Box>
      </Container>
    </Box>
  );
};

const ProtectedRoute = ({ children, user, allowedRoles }) => {
  if (!user) {
    return <Navigate to="/login" replace />;
  }

  if (!allowedRoles.includes(user.role)) {
    return <Navigate to="/unauthorized" replace />;
  }

  return children;
};

// ===== AUTHENTICATION COMPONENTS =====

const LoginPage = ({ onLogin }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (role) => {
    setLoading(true);
    setError('');

    try {
      await new Promise(resolve => setTimeout(resolve, 1500));

      const users = {
        admin: { id: 1, name: 'System Admin', email: 'admin@lesotho.ls', role: 'admin' },
        institution: { id: 2, name: 'Limkokwing University', email: 'institution@limkokwing.ls', role: 'institution' },
        company: { id: 3, name: 'Econet Telecom', email: 'company@econet.ls', role: 'company' },
        student: { id: 4, name: 'Test Student', email: 'student@test.ls', role: 'student' }
      };

      onLogin(users[role]);
      navigate('/dashboard');
    } catch (err) {
      setError('Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { label: 'Student', icon: <Person />, role: 'student' },
    { label: 'Institution', icon: <Business />, role: 'institution' },
    { label: 'Company', icon: <BusinessCenter />, role: 'company' },
    { label: 'Admin', icon: <AdminPanelSettings />, role: 'admin' }
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      py: 4
    }}>
      <Container maxWidth="md">
        <Paper elevation={24} sx={{ borderRadius: 4, overflow: 'hidden' }}>
          <Box sx={{ bgcolor: 'primary.main', color: 'white', p: 4, textAlign: 'center' }}>
            <School sx={{ fontSize: 64, mb: 2 }} />
            <Typography variant="h3" fontWeight="bold" gutterBottom>
              Lesotho Career Platform
            </Typography>
            <Typography variant="h6">
              Your Gateway to Education and Career Success
            </Typography>
          </Box>

          <Box sx={{ p: 4 }}>
            <Typography variant="h5" align="center" gutterBottom sx={{ mb: 4 }}>
              Sign In to Your Account
            </Typography>

            {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

            <Paper sx={{ mb: 3 }}>
              <Tabs
                value={activeTab}
                onChange={(e, newValue) => setActiveTab(newValue)}
                variant="fullWidth"
              >
                {tabs.map((tab, index) => (
                  <Tab key={tab.role} icon={tab.icon} label={tab.label} />
                ))}
              </Tabs>
            </Paper>

            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                Sign in as {tabs[activeTab].label} to access your dashboard
              </Typography>
            </Box>

            <Button
              fullWidth
              variant="contained"
              size="large"
              onClick={() => handleLogin(tabs[activeTab].role)}
              disabled={loading}
              sx={{ py: 1.5, mb: 2 }}
            >
              {loading ? <CircularProgress size={24} /> : `Sign In as ${tabs[activeTab].label}`}
            </Button>

            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="body2" color="text.secondary">
                Demo Platform - Click any role to sign in
              </Typography>
            </Box>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

