                <Button 
                  variant="contained" 
                  startIcon={<Add />}
                  onClick={() => setActiveTab(1)}
                >
                  Add New Course
                </Button>
                <Button 
                  variant="outlined" 
                  startIcon={<Assignment />}
                  onClick={() => setActiveTab(2)}
                >
                  Review Applications
                </Button>
