                            disabled={app.status === 'admitted'}
                          >
                            <CheckCircle />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Reject Application">
                          <IconButton 
                            color="error" 
                            onClick={() => updateApplicationStatus(app.id, 'rejected')}
                            disabled={app.status === 'rejected'}
                          >
                            <Delete />
                          </IconButton>
                        </Tooltip>
