import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import {
  Box,
  Container,
  Paper,
  Typography,
  CircularProgress,
  Fade
} from '@mui/material';
import { Security, ErrorOutline } from '@mui/icons-material';

const ProtectedRoute = ({ 
  children, 
  user, 
  requiredRole, 
  loading = false,
  fallbackPath = '/',
  showLoading = true
}) => {
  const location = useLocation();

  // Show loading spinner while checking authentication
  if (loading && showLoading) {
    return (
      <Container maxWidth="sm" sx={{ py: 8 }}>
        <Fade in={true} timeout={500}>
          <Paper 
            elevation={0}
            sx={{ 
              p: 6, 
              textAlign: 'center',
              bgcolor: 'transparent'
            }}
          >
            <CircularProgress 
              size={60} 
              thickness={4}
              sx={{ 
                color: 'primary.main',
                mb: 3
              }} 
            />
            <Typography variant="h6" color="text.secondary">
              Verifying access...
            </Typography>
          </Paper>
        </Fade>
      </Container>
    );
  }

  // Check if user is authenticated
  if (!user) {
    return (
      <Navigate 
        to={fallbackPath} 
        state={{ from: location, message: 'Please log in to access this page' }} 
        replace 
      />
    );
  }

  // Check if user has required role
  if (requiredRole && user.role !== requiredRole) {
    return (
      <Container maxWidth="md" sx={{ py: 8 }}>
        <Fade in={true} timeout={500}>
          <Paper 
            elevation={3}
            sx={{ 
              p: 6, 
              textAlign: 'center',
              borderRadius: 3
            }}
          >
            <ErrorOutline 
              sx={{ 
                fontSize: 64, 
                color: 'error.main',
                mb: 3
              }} 
            />
            <Typography variant="h4" gutterBottom color="error">
              Access Denied
            </Typography>
            <Typography variant="h6" color="text.secondary" gutterBottom>
              You don't have permission to access this page.
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
              This area requires <strong>{requiredRole}</strong> privileges, 
              but you are logged in as <strong>{user.role}</strong>.
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
              <Typography 
                variant="body2" 
                sx={{ 
                  p: 2, 
                  bgcolor: 'grey.100',
                  borderRadius: 2,
                  fontFamily: 'monospace'
                }}
              >
                Required: {requiredRole} | Current: {user.role}
              </Typography>
            </Box>
          </Paper>
        </Fade>
      </Container>
    );
  }

  // Check if user's email is verified (if required)
  if (user.requireEmailVerification && !user.emailVerified) {
    return (
      <Container maxWidth="md" sx={{ py: 8 }}>
        <Fade in={true} timeout={500}>
          <Paper 
            elevation={3}
            sx={{ 
              p: 6, 
              textAlign: 'center',
              borderRadius: 3
            }}
          >
            <Security 
              sx={{ 
                fontSize: 64, 
                color: 'warning.main',
                mb: 3
              }} 
            />
            <Typography variant="h4" gutterBottom>
              Email Verification Required
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
              Please verify your email address before accessing this page. 
              Check your inbox for the verification link.
            </Typography>
          </Paper>
        </Fade>
      </Container>
    );
  }

  // If all checks pass, render the children
  return children;
};

// Higher-order component for specific role protection
export const withRoleProtection = (Component, requiredRole) => {
  return (props) => (
    <ProtectedRoute requiredRole={requiredRole}>
      <Component {...props} />
    </ProtectedRoute>
  );
};

// Specific protected route components for different user types
export const StudentRoute = ({ children, user, loading }) => (
  <ProtectedRoute user={user} requiredRole="student" loading={loading}>
    {children}
  </ProtectedRoute>
);

export const InstituteRoute = ({ children, user, loading }) => (
  <ProtectedRoute user={user} requiredRole="institute" loading={loading}>
    {children}
  </ProtectedRoute>
);

export const CompanyRoute = ({ children, user, loading }) => (
  <ProtectedRoute user={user} requiredRole="company" loading={loading}>
    {children}
  </ProtectedRoute>
);

export const AdminRoute = ({ children, user, loading }) => (
  <ProtectedRoute user={user} requiredRole="admin" loading={loading}>
    {children}
  </ProtectedRoute>
);

export default ProtectedRoute;
