import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container,
  Typography,
  Button,
  Paper,
  TextField,
  Alert,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  AppBar,
  Toolbar,
  IconButton,
  Avatar,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Tabs,
  Tab,
  Snackbar,
  CardActions,
  CardHeader,
  Divider,
  Tooltip
} from '@mui/material';
import {
  School,
  Business,
  BusinessCenter,
  AdminPanelSettings,
  Person,
  ExitToApp,
  Dashboard as DashboardIcon,
  People,
  Work,
  Assignment,
  CheckCircle,
  Warning,
  TrendingUp,
  Email,
  Phone,
  Add,
  Edit,
  Delete,
  Visibility,
  Notifications,
  DateRange,
  Refresh
} from '@mui/icons-material';

// ===== THEME CONFIGURATION =====
const theme = createTheme({
  palette: {
    primary: {
      main: '#2E7D32',
      light: '#4CAF50',
      dark: '#1B5E20'
    },
    secondary: {
      main: '#FF6F00',
      light: '#FF9800',
      dark: '#E65100'
    },
    background: {
      default: '#f8f9fa',
      paper: '#ffffff'
    }
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
  }
});

// ===== DATA STORAGE & CONTEXT =====
const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Initialize data
  const [institutions, setInstitutions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [students, setStudents] = useState([]);
  const [applications, setApplications] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [jobApplications, setJobApplications] = useState([]);

  useEffect(() => {
    initializeData();
    const savedUser = localStorage.getItem('currentUser');
    if (savedUser) {
      const user = JSON.parse(savedUser);
      setCurrentUser(user);
    }
    setLoading(false);
  }, []);

  const initializeData = () => {
    // Check if data already exists in localStorage
    const savedInstitutions = localStorage.getItem('institutions');
    const savedCompanies = localStorage.getItem('companies');
    const savedStudents = localStorage.getItem('students');
    const savedApplications = localStorage.getItem('applications');
    const savedJobs = localStorage.getItem('jobs');
    const savedJobApplications = localStorage.getItem('jobApplications');

    if (savedInstitutions && savedCompanies && savedStudents) {
      // Load existing data
      setInstitutions(JSON.parse(savedInstitutions));
      setCompanies(JSON.parse(savedCompanies));
      setStudents(JSON.parse(savedStudents));
      setApplications(JSON.parse(savedApplications || '[]'));
      setJobs(JSON.parse(savedJobs || '[]'));
      setJobApplications(JSON.parse(savedJobApplications || '[]'));
    } else {
      // Initialize with sample data only if no data exists
      const sampleInstitutions = [
        {
          id: 1,
          name: 'Limkokwing University of Creative Technology',
          email: 'admin@limkokwing.ls',
          type: 'University',
          location: 'Maseru',
          phone: '+266 2231 7242',
          status: 'approved',
          faculties: [
            {
              id: 1,
              name: 'Faculty of Information & Communication Technology',
              courses: [
                { id: 1, name: 'BSc in Information Technology', duration: '4 years', requirements: 'LGCSE with Math and English' },
                { id: 2, name: 'BSc in Software Engineering', duration: '4 years', requirements: 'LGCSE with Math and Physics' }
              ]
            }
          ]
        }
      ];

      const sampleCompanies = [
        {
          id: 1,
          name: 'Econet Telecom Lesotho',
          email: 'hr@econet.ls',
          industry: 'Telecommunications',
          location: 'Maseru',
          phone: '+266 2232 0000',
          status: 'approved'
        }
      ];

      const sampleStudents = [
        {
          id: 1,
          name: 'Test Student',
          email: 'student@test.ls',
          phone: '+266 5954 1234',
          location: 'Maseru',
          qualifications: {},
          status: 'active'
        }
      ];

      const sampleJobs = [
        {
          id: 1,
          title: 'Junior Software Developer',
          company: 'Econet Telecom Lesotho',
          companyId: 1,
          location: 'Maseru',
          type: 'Full-time',
          salary: 'M8,000 - M12,000',
          description: 'We are looking for a passionate Junior Software Developer to design, develop and install software solutions.',
          requirements: 'BSc in Computer Science or related field',
          postedDate: '2024-01-15',
          status: 'active'
        }
      ];

      setInstitutions(sampleInstitutions);
      setCompanies(sampleCompanies);
      setStudents(sampleStudents);
      setJobs(sampleJobs);

      // Save to localStorage
      localStorage.setItem('institutions', JSON.stringify(sampleInstitutions));
      localStorage.setItem('companies', JSON.stringify(sampleCompanies));
      localStorage.setItem('students', JSON.stringify(sampleStudents));
      localStorage.setItem('jobs', JSON.stringify(sampleJobs));
      localStorage.setItem('applications', JSON.stringify([]));
      localStorage.setItem('jobApplications', JSON.stringify([]));
    }
  };

  // Save data to localStorage whenever it changes
  useEffect(() => {
    if (institutions.length > 0) {
      localStorage.setItem('institutions', JSON.stringify(institutions));
    }
  }, [institutions]);

  useEffect(() => {
    if (companies.length > 0) {
      localStorage.setItem('companies', JSON.stringify(companies));
    }
  }, [companies]);

  useEffect(() => {
    if (students.length > 0) {
      localStorage.setItem('students', JSON.stringify(students));
    }
  }, [students]);

  useEffect(() => {
    localStorage.setItem('applications', JSON.stringify(applications));
  }, [applications]);

  useEffect(() => {
    localStorage.setItem('jobs', JSON.stringify(jobs));
  }, [jobs]);

  useEffect(() => {
    localStorage.setItem('jobApplications', JSON.stringify(jobApplications));
  }, [jobApplications]);

  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const login = (email, password) => {
    // First check demo users
    const demoUsers = {
      'admin@lesotho.ls': { 
        id: 1, 
        name: 'System Administrator', 
        email: 'admin@lesotho.ls', 
        password: 'admin123', 
        role: 'admin' 
      },
      'institution@limkokwing.ls': { 
        id: 2, 
        name: 'Limkokwing University', 
        email: 'institution@limkokwing.ls', 
        password: 'inst123', 
        role: 'institution',
        institutionId: 1
      },
      'company@econet.ls': { 
        id: 3, 
        name: 'Econet Telecom', 
        email: 'company@econet.ls', 
        password: 'company123', 
        role: 'company',
        companyId: 1
      },
      'student@test.ls': { 
        id: 4, 
        name: 'Test Student', 
        email: 'student@test.ls', 
        password: 'student123', 
        role: 'student',
        studentId: 1
      }
    };

    let user = demoUsers[email];

    // If not demo user, check registered students
    if (!user) {
      const registeredStudent = students.find(s => s.email === email);
      if (registeredStudent && registeredStudent.password === password) {
        user = {
          id: registeredStudent.id,
          name: registeredStudent.name,
          email: registeredStudent.email,
          role: 'student',
          studentId: registeredStudent.id
        };
      }
    }

    if (user && user.password === password) {
      // Remove password from user object before saving
      const { password: _, ...userWithoutPassword } = user;
      setCurrentUser(userWithoutPassword);
      localStorage.setItem('currentUser', JSON.stringify(userWithoutPassword));
      showSnackbar(`Welcome back, ${user.name}!`, 'success');
      return { success: true, user: userWithoutPassword };
    }
    return { success: false, error: 'Invalid email or password' };
  };

  const register = (userData) => {
    console.log('Registering user:', userData);

    // Check if user already exists
    const existingUser = students.find(s => s.email === userData.email);
    if (existingUser) {
      return { success: false, error: 'User already exists with this email' };
    }

    const newStudentId = Date.now();
    const newStudent = {
      id: newStudentId,
      name: userData.name,
      email: userData.email,
      password: userData.password, // Store password for login
      phone: userData.phone || '',
      location: userData.location || '',
      qualifications: {},
      status: 'active'
    };

    console.log('New student created:', newStudent);

    // Add to students array
    setStudents(prev => {
      const updatedStudents = [...prev, newStudent];
      console.log('Updated students:', updatedStudents);
      return updatedStudents;
    });

    // Create user object without password for session
    const newUser = {
      id: newStudentId,
      name: userData.name,
      email: userData.email,
      role: 'student',
      studentId: newStudentId
    };

    console.log('New user for session:', newUser);

    // Set current user and save to localStorage
    setCurrentUser(newUser);
    localStorage.setItem('currentUser', JSON.stringify(newUser));

    showSnackbar('Registration successful! Welcome to the platform.', 'success');
    return { success: true, user: newUser };
  };

  const logout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    showSnackbar('Logged out successfully', 'info');
  };

  const submitApplication = (applicationData) => {
    const institutionApplications = applications.filter(app => 
      app.studentId === applicationData.studentId && app.institutionId === applicationData.institutionId
    );

    if (institutionApplications.length >= 2) {
      showSnackbar('You can only apply for maximum 2 courses per institution', 'error');
      return { success: false };
    }

    const newApplication = {
      id: Date.now(),
      ...applicationData,
      status: 'pending',
      appliedDate: new Date().toISOString().split('T')[0]
    };

    setApplications(prev => [...prev, newApplication]);
    showSnackbar('Application submitted successfully!', 'success');
    return { success: true };
  };

  const applyForJob = (jobApplicationData) => {
    const newJobApplication = {
      id: Date.now(),
      ...jobApplicationData,
      appliedDate: new Date().toISOString().split('T')[0],
      status: 'pending'
    };
    setJobApplications(prev => [...prev, newJobApplication]);
    showSnackbar('Job application submitted!', 'success');
    return { success: true };
  };

  const value = {
    currentUser,
    institutions,
    companies,
    students,
    applications,
    jobs,
    jobApplications,
    login,
    register,
    logout,
    submitApplication,
    applyForJob,
    showSnackbar
  };

  return (
    <AppContext.Provider value={value}>
      {children}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={4000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        message={snackbar.message}
      />
    </AppContext.Provider>
  );
};

const useApp = () => {
  const context = React.useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
};

// ===== COMPONENTS =====
const Navbar = () => {
  const navigate = useNavigate();
  const { currentUser, logout } = useApp();

  const getRoleIcon = () => {
    switch(currentUser?.role) {
      case 'admin': return <AdminPanelSettings />;
      case 'institution': return <Business />;
      case 'company': return <BusinessCenter />;
      default: return <Person />;
    }
  };

  return (
    <AppBar position="static" sx={{ bgcolor: 'primary.main' }}>
      <Toolbar>
        <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1, cursor: 'pointer' }} onClick={() => navigate('/')}>
          <School sx={{ mr: 2 }} />
          <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
            Lesotho Career Platform
          </Typography>
        </Box>

        {currentUser ? (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Chip 
              icon={getRoleIcon()} 
              label={`${currentUser.name} (${currentUser.role})`} 
              variant="outlined" 
              sx={{ color: 'white', borderColor: 'white' }}
            />
            <Button 
              color="inherit" 
              onClick={() => {
                logout();
                navigate('/');
              }} 
              startIcon={<ExitToApp />}
            >
              Logout
            </Button>
          </Box>
        ) : (
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button color="inherit" onClick={() => navigate('/login')}>Login</Button>
            <Button color="inherit" variant="outlined" onClick={() => navigate('/register')}>Register</Button>
          </Box>
        )}
      </Toolbar>
    </AppBar>
  );
};

// ===== AUTH PAGES =====
const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login } = useApp();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const result = login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.error);
    }
    setLoading(false);
  };

  const demoCredentials = [
    { role: 'Admin', email: 'admin@lesotho.ls', password: 'admin123' },
    { role: 'Institution', email: 'institution@limkokwing.ls', password: 'inst123' },
    { role: 'Company', email: 'company@econet.ls', password: 'company123' },
    { role: 'Student', email: 'student@test.ls', password: 'student123' }
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      py: 4
    }}>
      <Container maxWidth="md">
        <Grid container spacing={4} alignItems="center">
          <Grid item xs={12} md={6}>
            <Paper elevation={24} sx={{ borderRadius: 4, overflow: 'hidden' }}>
              <Box sx={{ bgcolor: 'primary.main', color: 'white', p: 4, textAlign: 'center' }}>
                <School sx={{ fontSize: 48, mb: 2 }} />
                <Typography variant="h4" fontWeight="bold" gutterBottom>
                  Welcome Back
                </Typography>
                <Typography variant="body1">
                  Sign in to your account
                </Typography>
              </Box>

              <Box sx={{ p: 4 }}>
                {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

                <form onSubmit={handleSubmit}>
                  <TextField
                    fullWidth
                    label="Email Address"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                    required
                    sx={{ mb: 3 }}
                  />
                  <TextField
                    fullWidth
                    label="Password"
                    type="password"
                    value={formData.password}
                    onChange={(e) => setFormData({...formData, password: e.target.value})}
                    required
                    sx={{ mb: 3 }}
                  />

                  <Button
                    fullWidth
                    type="submit"
                    variant="contained"
                    size="large"
                    disabled={loading}
                    sx={{ py: 1.5, mb: 2 }}
                  >
                    {loading ? <CircularProgress size={24} /> : 'Sign In'}
                  </Button>
                </form>

                <Box sx={{ textAlign: 'center' }}>
                  <Typography variant="body2" color="text.secondary">
                    Don't have an account?{' '}
                    <Button 
                      color="primary" 
                      onClick={() => navigate('/register')}
                      sx={{ textTransform: 'none' }}
                    >
                      Sign up here
                    </Button>
                  </Typography>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12} md={6}>
            <Box sx={{ color: 'white' }}>
              <Typography variant="h4" fontWeight="bold" gutterBottom>
                Demo Credentials
              </Typography>
              <Typography variant="body1" sx={{ mb: 4, opacity: 0.9 }}>
                Use any of these test accounts:
              </Typography>
              <Grid container spacing={2}>
                {demoCredentials.map((cred, index) => (
                  <Grid item xs={12} sm={6} key={index}>
                    <Card sx={{ bgcolor: 'rgba(255,255,255,0.1)' }}>
                      <CardContent>
                        <Typography variant="h6" sx={{ color: 'white', mb: 1 }}>
                          {cred.role}
                        </Typography>
                        <Typography variant="body2" sx={{ color: 'white', opacity: 0.8, mb: 1 }}>
                          Email: {cred.email}
                        </Typography>
                        <Typography variant="body2" sx={{ color: 'white', opacity: 0.8, mb: 2 }}>
                          Password: {cred.password}
                        </Typography>
                        <Button
                          size="small"
                          variant="outlined"
                          sx={{ color: 'white', borderColor: 'white' }}
                          onClick={() => setFormData({ email: cred.email, password: cred.password })}
                        >
                          Use This
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    location: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { register } = useApp();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      setLoading(false);
      return;
    }

    const userData = {
      ...formData,
      role: 'student' // Force student role for registration
    };

    console.log('Submitting registration:', userData);

    const result = register(userData);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.error);
    }
    setLoading(false);
  };

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      py: 4
    }}>
      <Container maxWidth="sm">
        <Paper elevation={24} sx={{ borderRadius: 4, overflow: 'hidden' }}>
          <Box sx={{ bgcolor: 'primary.main', color: 'white', p: 4, textAlign: 'center' }}>
            <School sx={{ fontSize: 48, mb: 2 }} />
            <Typography variant="h4" fontWeight="bold" gutterBottom>
              Create Student Account
            </Typography>
            <Typography variant="body1">
              Join the Lesotho Career Platform
            </Typography>
          </Box>

          <Box sx={{ p: 4 }}>
            {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

            <form onSubmit={handleSubmit}>
              <TextField
                fullWidth
                label="Full Name"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                required
                sx={{ mb: 3 }}
              />

              <TextField
                fullWidth
                label="Email Address"
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
                sx={{ mb: 3 }}
              />

              <TextField
                fullWidth
                label="Phone Number"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
                sx={{ mb: 3 }}
              />

              <TextField
                fullWidth
                label="Location"
                value={formData.location}
                onChange={(e) => setFormData({...formData, location: e.target.value})}
                sx={{ mb: 3 }}
              />

              <TextField
                fullWidth
                label="Password"
                type="password"
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                required
                sx={{ mb: 3 }}
              />

              <TextField
                fullWidth
                label="Confirm Password"
                type="password"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                required
                sx={{ mb: 3 }}
              />

              <Button
                fullWidth
                type="submit"
                variant="contained"
                size="large"
                disabled={loading}
                sx={{ py: 1.5, mb: 2 }}
              >
                {loading ? <CircularProgress size={24} /> : 'Create Account'}
              </Button>
            </form>

            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="body2" color="text.secondary">
                Already have an account?{' '}
                <Button 
                  color="primary" 
                  onClick={() => navigate('/login')}
                  sx={{ textTransform: 'none' }}
                >
                  Sign in here
                </Button>
              </Typography>
            </Box>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

// ===== STUDENT DASHBOARD =====
const StudentDashboard = () => {
  const { currentUser, institutions, applications, jobs, submitApplication, applyForJob, showSnackbar } = useApp();
  const [selectedInstitution, setSelectedInstitution] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [viewJobDialog, setViewJobDialog] = useState(null);

  console.log('StudentDashboard rendered with:', { currentUser, institutions, applications, jobs });

  // Ensure we have the necessary data
  const studentApplications = applications.filter(app => app.studentId === currentUser?.studentId);
  const availableJobs = jobs.filter(job => job.status === 'active');

  const handleApplication = () => {
    if (!selectedInstitution || !selectedCourse) {
      showSnackbar('Please select both institution and course', 'error');
      return;
    }

    const institution = institutions.find(inst => inst.id === parseInt(selectedInstitution));
    const course = institution.faculties.flatMap(f => f.courses).find(c => c.id === parseInt(selectedCourse));

    const result = submitApplication({
      studentId: currentUser.studentId,
      studentName: currentUser.name,
      institutionId: institution.id,
      institutionName: institution.name,
      courseId: course.id,
      courseName: course.name,
      faculty: institution.faculties.find(f => f.courses.some(c => c.id === course.id)).name
    });

    if (result.success) {
      setSelectedInstitution('');
      setSelectedCourse('');
    }
  };

  const handleJobApplication = (job) => {
    const result = applyForJob({
      jobId: job.id,
      jobTitle: job.title,
      companyId: job.companyId,
      companyName: job.company,
      studentId: currentUser.studentId,
      studentName: currentUser.name
    });

    if (result.success) {
      setViewJobDialog(null);
    }
  };

  const statsCards = [
    { 
      title: 'My Applications', 
      value: studentApplications.length, 
      color: '#2196f3', 
      icon: <Assignment />,
      description: 'Total course applications'
    },
    { 
      title: 'Admitted', 
      value: studentApplications.filter(app => app.status === 'admitted').length, 
      color: '#4caf50', 
      icon: <CheckCircle />,
      description: 'Successful admissions'
    },
    { 
      title: 'Pending', 
      value: studentApplications.filter(app => app.status === 'pending').length, 
      color: '#ff9800', 
      icon: <Warning />,
      description: 'Applications under review'
    },
    { 
      title: 'Job Matches', 
      value: availableJobs.length, 
      color: '#9c27b0', 
      icon: <Work />,
      description: 'Available job opportunities'
    }
  ];

  if (!currentUser) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
          Welcome to your Dashboard, {currentUser?.name}!
        </Typography>
        <Typography variant="h6" color="text.secondary">
          Track your applications and explore career opportunities
        </Typography>
      </Box>

      {/* Statistics Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        {statsCards.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card 
              sx={{ 
                background: `linear-gradient(135deg, ${stat.color} 0%, ${stat.color}99 100%)`,
                color: 'white',
                transition: 'transform 0.2s',
                '&:hover': { transform: 'translateY(-4px)' }
              }}
            >
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Box>
                    <Typography variant="h3" fontWeight="bold">{stat.value}</Typography>
                    <Typography variant="h6">{stat.title}</Typography>
                    <Typography variant="body2" sx={{ opacity: 0.9, mt: 1 }}>
                      {stat.description}
                    </Typography>
                  </Box>
                  <Box sx={{ opacity: 0.9, fontSize: '3rem' }}>{stat.icon}</Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Paper sx={{ mb: 4 }}>
        <Tabs 
          value={activeTab} 
          onChange={(e, newValue) => setActiveTab(newValue)}
          sx={{ borderBottom: 1, borderColor: 'divider' }}
        >
          <Tab label="Apply for Courses" />
          <Tab label="My Applications" />
          <Tab label="Job Opportunities" />
          <Tab label="My Profile" />
        </Tabs>
      </Paper>

      {activeTab === 0 && (
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <Card sx={{ p: 3 }}>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                Apply for Courses
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                <FormControl fullWidth>
                  <InputLabel>Select Institution</InputLabel>
                  <Select
                    value={selectedInstitution}
                    onChange={(e) => {
                      setSelectedInstitution(e.target.value);
                      setSelectedCourse('');
                    }}
                    label="Select Institution"
                  >
                    {institutions.filter(inst => inst.status === 'approved').map(inst => (
                      <MenuItem key={inst.id} value={inst.id}>
                        {inst.name} - {inst.location}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <FormControl fullWidth disabled={!selectedInstitution}>
                  <InputLabel>Select Course</InputLabel>
                  <Select
                    value={selectedCourse}
                    onChange={(e) => setSelectedCourse(e.target.value)}
                    label="Select Course"
                  >
                    {selectedInstitution && institutions
                      .find(inst => inst.id === parseInt(selectedInstitution))
                      ?.faculties.flatMap(f => f.courses)
                      .map(course => (
                        <MenuItem key={course.id} value={course.id}>
                          {course.name} - {course.duration}
                        </MenuItem>
                      ))
                    }
                  </Select>
                </FormControl>

                {selectedCourse && (
                  <Box sx={{ p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      <strong>Requirements:</strong>{' '}
                      {institutions
                        .find(inst => inst.id === parseInt(selectedInstitution))
                        ?.faculties.flatMap(f => f.courses)
                        .find(c => c.id === parseInt(selectedCourse))?.requirements}
                    </Typography>
                  </Box>
                )}

                <Button
                  variant="contained"
                  size="large"
                  onClick={handleApplication}
                  disabled={!selectedInstitution || !selectedCourse}
                  startIcon={<Add />}
                  sx={{ mt: 2 }}
                >
                  Submit Application
                </Button>
              </Box>
            </Card>
          </Grid>

          <Grid item xs={12} md={6}>
            <Card sx={{ p: 3 }}>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                Available Institutions
              </Typography>
              <List>
                {institutions.filter(inst => inst.status === 'approved').map(inst => (
                  <ListItem key={inst.id}>
                    <ListItemIcon>
                      <Business color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary={inst.name}
                      secondary={`${inst.type}  ${inst.location}  ${inst.faculties.flatMap(f => f.courses).length} courses`}
                    />
                  </ListItem>
                ))}
              </List>
            </Card>
          </Grid>
        </Grid>
      )}

      {activeTab === 1 && (
        <Card>
          <CardHeader
            title="My Applications"
            action={
              <Button startIcon={<Refresh />} onClick={() => window.location.reload()}>
                Refresh
              </Button>
            }
          />
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Institution</TableCell>
                  <TableCell>Course</TableCell>
                  <TableCell>Faculty</TableCell>
                  <TableCell>Applied Date</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {studentApplications.map((app) => (
                  <TableRow key={app.id}>
                    <TableCell>{app.institutionName}</TableCell>
                    <TableCell>{app.courseName}</TableCell>
                    <TableCell>{app.faculty}</TableCell>
                    <TableCell>{app.appliedDate}</TableCell>
                    <TableCell>
                      <Chip 
                        label={app.status} 
                        color={
                          app.status === 'admitted' ? 'success' : 
                          app.status === 'rejected' ? 'error' : 'warning'
                        }
                      />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Card>
      )}

      {activeTab === 2 && (
        <Box>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main', mb: 3 }}>
            Available Job Opportunities
          </Typography>
          <Grid container spacing={3}>
            {availableJobs.map((job) => (
              <Grid item xs={12} md={6} key={job.id}>
                <Card sx={{ p: 3, height: '100%' }}>
                  <CardHeader
                    avatar={<BusinessCenter color="primary" />}
                    title={job.title}
                    subheader={`${job.company}  ${job.location}`}
                  />
                  <CardContent>
                    <Typography variant="body2" color="text.secondary" paragraph>
                      {job.description}
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                      <Chip label={job.type} color="primary" size="small" />
                      <Chip label={job.salary} variant="outlined" size="small" />
                    </Box>
                  </CardContent>
                  <CardActions>
                    <Button 
                      size="small" 
                      startIcon={<Visibility />}
                      onClick={() => setViewJobDialog(job)}
                    >
                      View Details
                    </Button>
                    <Button 
                      size="small" 
                      variant="contained"
                      onClick={() => handleJobApplication(job)}
                    >
                      Apply Now
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}

      {/* View Job Dialog */}
      <Dialog 
        open={!!viewJobDialog} 
        onClose={() => setViewJobDialog(null)}
        maxWidth="md"
        fullWidth
      >
        {viewJobDialog && (
          <>
            <DialogTitle>
              <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
                {viewJobDialog.title}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {viewJobDialog.company}  {viewJobDialog.location}
              </Typography>
            </DialogTitle>
            <DialogContent>
              <Grid container spacing={3} sx={{ mt: 1 }}>
                <Grid item xs={12} md={6}>
                  <Typography variant="subtitle1" fontWeight="bold">Job Type</Typography>
                  <Chip label={viewJobDialog.type} color="primary" />
                </Grid>
                <Grid item xs={12} md={6}>
                  <Typography variant="subtitle1" fontWeight="bold">Salary</Typography>
                  <Typography>{viewJobDialog.salary}</Typography>
                </Grid>
                <Grid item xs={12}>
                  <Typography variant="subtitle1" fontWeight="bold">Job Description</Typography>
                  <Typography variant="body2" paragraph>
                    {viewJobDialog.description}
                  </Typography>
                </Grid>
                <Grid item xs={12}>
                  <Typography variant="subtitle1" fontWeight="bold">Requirements</Typography>
                  <Typography variant="body2">
                    {viewJobDialog.requirements}
                  </Typography>
                </Grid>
              </Grid>
            </DialogContent>
            <DialogActions>
              <Button onClick={() => setViewJobDialog(null)}>Close</Button>
              <Button 
                variant="contained" 
                onClick={() => handleJobApplication(viewJobDialog)}
                startIcon={<CheckCircle />}
              >
                Apply for this Job
              </Button>
            </DialogActions>
          </>
        )}
      </Dialog>
    </Container>
  );
};

// ===== PROTECTED ROUTE =====
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useApp();

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  return children;
};

// ===== LANDING PAGE =====
const LandingPage = () => {
  const navigate = useNavigate();
  const { currentUser } = useApp();

  if (currentUser) {
    return <Navigate to="/dashboard" />;
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Box sx={{ 
        bgcolor: 'primary.main', 
        color: 'white', 
        py: 12,
        background: 'linear-gradient(135deg, #2E7D32 0%, #4CAF50 100%)'
      }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography variant="h2" fontWeight="bold" gutterBottom>
                Your Future Starts Here
              </Typography>
              <Typography variant="h5" gutterBottom sx={{ mb: 4 }}>
                Connecting Lesotho's students with educational institutions and career opportunities
              </Typography>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <Button 
                  variant="contained" 
                  size="large" 
                  sx={{ bgcolor: 'white', color: 'primary.main', px: 4 }}
                  onClick={() => navigate('/register')}
                >
                  Get Started
                </Button>
                <Button 
                  variant="outlined" 
                  size="large" 
                  sx={{ borderColor: 'white', color: 'white', px: 4 }}
                  onClick={() => navigate('/login')}
                >
                  Sign In
                </Button>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box sx={{ textAlign: 'center' }}>
                <School sx={{ fontSize: 200, opacity: 0.8 }} />
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </Box>
  );
};

// ===== MAIN APP COMPONENT =====
const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppProvider>
        <Router>
          <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
            <Navbar />
            <Routes>
              <Route path="/" element={<LandingPage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <StudentDashboard />
                  </ProtectedRoute>
                } 
              />
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </Box>
        </Router>
      </AppProvider>
    </ThemeProvider>
  );
};

export default App;
