import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  useTheme,
  useMediaQuery,
  CssBaseline
} from '@mui/material';
import {
  Menu as MenuIcon,
  School,
  BusinessCenter,
  Dashboard,
  Person,
  Group
} from '@mui/icons-material';

// Import your components (you'll need to create these)
import Home from './components/Home';
import StudentRegister from './components/StudentRegister';
import StudentLogin from './components/StudentLogin';
import InstituteRegister from './components/InstituteRegister';
import CompanyRegister from './components/CompanyRegister';
import AdminLogin from './components/AdminLogin';

// Navigation Component
function Navigation() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const location = useLocation();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const menuItems = [
    { text: 'Home', path: '/', icon: <Dashboard /> },
    { text: 'For Students', path: '/student', icon: <School />, children: [
      { text: 'Register', path: '/student/register' },
      { text: 'Login', path: '/student/login' }
    ]},
    { text: 'For Institutions', path: '/institute', icon: <Person />, children: [
      { text: 'Register', path: '/institute/register' },
      { text: 'Login', path: '/institute/login' }
    ]},
    { text: 'For Companies', path: '/company', icon: <BusinessCenter />, children: [
      { text: 'Register', path: '/company/register' },
      { text: 'Login', path: '/company/login' }
    ]},
    { text: 'Admin', path: '/admin/login', icon: <Group /> }
  ];

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <Box sx={{ width: 250 }} onClick={handleDrawerToggle}>
      <Toolbar>
        <Typography variant="h6" color="primary" fontWeight="bold">
          CareerGuide
        </Typography>
      </Toolbar>
      <List>
        {menuItems.map((item) => (
          <ListItem 
            key={item.text} 
            component={Link} 
            to={item.path}
            sx={{
              backgroundColor: location.pathname === item.path ? theme.palette.primary.main : 'transparent',
              color: location.pathname === item.path ? 'white' : 'inherit',
              '&:hover': {
                backgroundColor: theme.palette.primary.light,
                color: 'white'
              }
            }}
          >
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <AppBar 
        position="static" 
        elevation={2}
        sx={{ 
          background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`
        }}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>

          <School sx={{ mr: 2 }} />

          <Typography 
            variant="h6" 
            component="div" 
            sx={{ 
              flexGrow: 1,
              fontWeight: 'bold',
              fontSize: { xs: '1.1rem', md: '1.25rem' }
            }}
          >
            Career Guidance Platform
          </Typography>

          {!isMobile && (
            <Box sx={{ display: 'flex', gap: 1 }}>
              {menuItems.map((item) => (
                <Button
                  key={item.text}
                  color="inherit"
                  component={Link}
                  to={item.path}
                  startIcon={item.icon}
                  sx={{
                    fontWeight: location.pathname === item.path ? 'bold' : 'normal',
                    borderBottom: location.pathname === item.path ? '2px solid white' : 'none',
                    borderRadius: 0
                  }}
                >
                  {item.text}
                </Button>
              ))}
            </Box>
          )}
        </Toolbar>
      </AppBar>

      <nav>
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{ keepMounted: true }}
          sx={{
            display: { xs: 'block', md: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 250 },
          }}
        >
          {drawer}
        </Drawer>
      </nav>
    </>
  );
}

// Main App Component
function App() {
  return (
    <Router>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        <Navigation />

        <Box component="main" sx={{ flexGrow: 1 }}>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Home />} />

            {/* Student Routes */}
            <Route path="/student/register" element={<StudentRegister />} />
            <Route path="/student/login" element={<StudentLogin />} />

            {/* Institute Routes */}
            <Route path="/institute/register" element={<InstituteRegister />} />
            <Route path="/institute/login" element={<InstituteLogin />} />

            {/* Company Routes */}
            <Route path="/company/register" element={<CompanyRegister />} />
            <Route path="/company/login" element={<CompanyLogin />} />

            {/* Admin Routes */}
            <Route path="/admin/login" element={<AdminLogin />} />

            {/* 404 Page */}
            <Route path="*" element={
              <Container sx={{ textAlign: 'center', py: 8 }}>
                <Typography variant="h3" gutterBottom>
                  404 - Page Not Found
                </Typography>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  The page you're looking for doesn't exist.
                </Typography>
                <Button 
                  variant="contained" 
                  component={Link} 
                  to="/"
                  sx={{ mt: 2 }}
                >
                  Go Back Home
                </Button>
              </Container>
            } />
          </Routes>
        </Box>

        {/* Footer */}
        <Box
          component="footer"
          sx={{
            py: 3,
            px: 2,
            mt: 'auto',
            backgroundColor: (theme) => theme.palette.grey[100],
            borderTop: (theme) => `1px solid ${theme.palette.grey[300]}`
          }}
        >
          <Container maxWidth="lg">
            <Typography variant="body2" color="text.secondary" align="center">
              © {new Date().getFullYear()} Career Guidance and Employment Integration Platform • 
              Faculty of Information & Communication Technology • 
              Limkokwing University of Creative Technology
            </Typography>
            <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
              Contact: ext. 117 • Mr Thokoana & Mr 'Molaoa
            </Typography>
          </Container>
        </Box>
      </Box>
    </Router>
  );
}

// Placeholder components for routes that don't exist yet
function InstituteLogin() {
  return (
    <Container sx={{ py: 8, textAlign: 'center' }}>
      <Typography variant="h3" gutterBottom>
        Institute Login
      </Typography>
      <Typography variant="h6" color="text.secondary">
        Institute login page coming soon...
      </Typography>
    </Container>
  );
}

function CompanyLogin() {
  return (
