// ===== MAIN APP COMPONENT =====

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress size={60} />
      </Box>
    );
  }

  const LandingPage = () => (
    <Box sx={{ flexGrow: 1 }}>
      {/* Hero Section */}
      <Box sx={{ 
        bgcolor: 'primary.main', 
        color: 'white', 
        py: 12,
        background: 'linear-gradient(135deg, #2E7D32 0%, #4CAF50 100%)'
      }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography variant="h2" fontWeight="bold" gutterBottom>
                Your Future Starts Here
              </Typography>
              <Typography variant="h5" gutterBottom sx={{ mb: 4, opacity: 0.9 }}>
                Connecting Lesotho's students with educational institutions and career opportunities
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <Button 
                  variant="contained" 
                  size="large" 
                  sx={{ bgcolor: 'white', color: 'primary.main', px: 4 }}
                  onClick={() => window.location.href = '/login'}
                >
                  Get Started
                </Button>
                <Button 
                  variant="outlined" 
                  size="large" 
                  sx={{ borderColor: 'white', color: 'white', px: 4 }}
                >
                  Learn More
                </Button>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box sx={{ textAlign: 'center' }}>
                <School sx={{ fontSize: 200, opacity: 0.8 }} />
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Features Section */}
      <Container sx={{ py: 8 }}>
        <Typography variant="h3" align="center" gutterBottom sx={{ fontWeight: 'bold', mb: 2 }}>
          How It Works
        </Typography>
        <Typography variant="h6" align="center" color="text.secondary" sx={{ mb: 6 }}>
          A complete ecosystem for education and career development
        </Typography>

        <Grid container spacing={4}>
          <Grid item xs={12} md={4}>
            <Card sx={{ p: 3, textAlign: 'center', height: '100%' }}>
              <School sx={{ fontSize: 64, color: 'primary.main', mb: 3 }} />
              <Typography variant="h5" gutterBottom>Find Your Course</Typography>
              <Typography variant="body1" color="text.secondary">
                Discover courses from top institutions in Lesotho that match your interests and career goals
              </Typography>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ p: 3, textAlign: 'center', height: '100%' }}>
              <Assignment sx={{ fontSize: 64, color: 'secondary.main', mb: 3 }} />
              <Typography variant="h5" gutterBottom>Apply Online</Typography>
              <Typography variant="body1" color="text.secondary">
                Streamlined application process with real-time tracking and notifications
              </Typography>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ p: 3, textAlign: 'center', height: '100%' }}>
              <Work sx={{ fontSize: 64, color: 'success.main', mb: 3 }} />
              <Typography variant="h5" gutterBottom>Start Your Career</Typography>
              <Typography variant="body1" color="text.secondary">
                Connect with employers and find job opportunities that match your qualifications
              </Typography>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
          {user && <Navbar user={user} onLogout={handleLogout} />}
          <Routes>
            <Route path="/" element={!user ? <LandingPage /> : <Navigate to="/dashboard" />} />
            <Route path="/login" element={!user ? <LoginPage onLogin={handleLogin} /> : <Navigate to="/dashboard" />} />
            <Route 
              path="/dashboard" 
              element={
                user ? (
                  user.role === 'admin' ? <AdminDashboard user={user} /> :
                  user.role === 'institution' ? <InstitutionDashboard user={user} /> :
                  user.role === 'company' ? <CompanyDashboard user={user} /> :
                  <StudentDashboard user={user} />
                ) : <Navigate to="/login" />
              } 
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
          {!user && <Footer />}
        </Box>
      </Router>
    </ThemeProvider>
  );
};

export default App;
