import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container
} from '@mui/material';

// Import Components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import ProtectedRoute from './components/ProtectedRoute';

// Auth Components
import Home from './components/Home';
import StudentLogin from './components/StudentLogin';
import StudentRegister from './components/StudentRegister';
import InstitutionLogin from './components/InstitutionLogin';
import CompanyLogin from './components/CompanyLogin';
import AdminLogin from './components/AdminLogin';

// Student Components
import StudentDashboard from './components/StudentDashboard';
import ApplyCourse from './components/ApplyCourse';
import ViewJob from './components/ViewJob';

// Institution Components
import ManageApplications from './components/ManageApplications';
import ManageCourse from './components/ManageCourse';

// Company Components
import PostJob from './components/PostJob';
import ViewApplicants from './components/ViewApplicants';

// Admin Components
import ManageCompanies from './components/ManageCompanies';
import ManageInstitution from './components/ManageInstitution';
import Reports from './components/Reports';

// Placeholder Components for missing pages
function InstituteRegister() {
  return <PlaceholderPage title="Institute Register" />;
}

function CompanyRegister() {
  return <PlaceholderPage title="Company Register" />;
}

function PlaceholderPage({ title }) {
  return (
    <Container sx={{ py: 8, textAlign: 'center' }}>
      <Typography variant="h3" gutterBottom>{title}</Typography>
      <Typography variant="h6" color="text.secondary">This page is coming soon...</Typography>
    </Container>
  );
}

// Create theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
      light: '#42a5f5',
      dark: '#1565c0',
    },
    secondary: {
      main: '#dc004e',
      light: '#ff5983',
      dark: '#9a0036',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 700,
    },
    h3: {
      fontWeight: 600,
    },
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          borderRadius: 8,
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

// Main App Component
function AppContent() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const location = useLocation();

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const handleRegister = (userData) => {
    setUser(userData);
  };

  // Check if current route is an auth page (to hide navbar/footer)
  const isAuthPage = [
    '/student/login',
    '/student/register', 
    '/institute/login',
    '/institute/register',
    '/company/login',
    '/company/register',
    '/admin/login'
  ].includes(location.pathname);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {!isAuthPage && <Navbar user={user} onLogout={handleLogout} />}

      <Box component="main" sx={{ flexGrow: 1 }}>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />

          {/* Authentication Routes */}
          <Route 
            path="/student/login" 
            element={<StudentLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/student/register" 
            element={<StudentRegister onRegister={handleRegister} />} 
          />
          <Route 
            path="/institute/login" 
            element={<InstitutionLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/institute/register" 
            element={<InstituteRegister />} 
          />
          <Route 
            path="/company/login" 
            element={<CompanyLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/company/register" 
            element={<CompanyRegister />} 
          />
          <Route 
            path="/admin/login" 
            element={<AdminLogin onLogin={handleLogin} />} 
          />

          {/* Student Protected Routes */}
          <Route 
            path="/student/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <StudentDashboard user={user} />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/apply-course/:courseId" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <ApplyCourse />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/jobs/:jobId" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <ViewJob />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/courses" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <PlaceholderPage title="Browse Courses" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/jobs" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <PlaceholderPage title="Browse Jobs" />
              </ProtectedRoute>
            } 
          />

          {/* Institution Protected Routes */}
          <Route 
            path="/institute/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <PlaceholderPage title="Institute Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/institute/applications" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <ManageApplications />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/institute/courses" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <ManageCourse />
              </ProtectedRoute>
            } 
          />

          {/* Company Protected Routes */}
          <Route 
            path="/company/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <PlaceholderPage title="Company Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/company/post-job" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <PostJob />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/company/applicants" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <ViewApplicants />
              </ProtectedRoute>
            } 
          />

          {/* Admin Protected Routes */}
          <Route 
            path="/admin/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <PlaceholderPage title="Admin Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/companies" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <ManageCompanies />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/institutions" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <ManageInstitution />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/reports" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <Reports />
              </ProtectedRoute>
            } 
          />

          {/* 404 Page */}
          <Route path="*" element={
            <Container sx={{ textAlign: 'center', py: 8 }}>
              <Typography variant="h3" gutterBottom>404 - Page Not Found</Typography>
              <Typography variant="h6" color="text.secondary" gutterBottom>
                The page you're looking for doesn't exist.
              </Typography>
              <Button variant="contained" component={Link} to="/" sx={{ mt: 2 }}>
                Go Back Home
              </Button>
            </Container>
          } />
        </Routes>
      </Box>

      {!isAuthPage && <Footer />}
    </Box>
  );
}

