const StudentDashboard = () => {
  const { currentUser, institutions, applications, jobs, submitApplication, showSnackbar } = useApp();
  const [selectedInstitution, setSelectedInstitution] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [activeTab, setActiveTab] = useState(0);

  const studentApplications = applications.filter(app => app.studentId === currentUser.studentId);
  const availableJobs = jobs.filter(job => job.status === 'active');

  const handleApplication = () => {
    if (!selectedInstitution || !selectedCourse) {
      showSnackbar('Please select both institution and course', 'error');
      return;
    }

    const institution = institutions.find(inst => inst.id === parseInt(selectedInstitution));
    const course = institution.faculties.flatMap(f => f.courses).find(c => c.id === parseInt(selectedCourse));

    const result = submitApplication({
      studentId: currentUser.studentId,
      studentName: currentUser.name,
      institutionId: institution.id,
      institutionName: institution.name,
      courseId: course.id,
      courseName: course.name,
      faculty: institution.faculties.find(f => f.courses.some(c => c.id === course.id)).name
    });

    if (result.success) {
      setSelectedInstitution('');
      setSelectedCourse('');
    }
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main', mb: 4 }}>
        Welcome back, {currentUser?.name}!
      </Typography>

      <Paper sx={{ mb: 4 }}>
        <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)}>
          <Tab label="Apply for Courses" />
          <Tab label="My Applications" />
          <Tab label="Job Opportunities" />
          <Tab label="Profile" />
        </Tabs>
      </Paper>

      {activeTab === 0 && (
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <Card sx={{ p: 3 }}>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                Apply for Courses
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                <FormControl fullWidth>
                  <InputLabel>Select Institution</InputLabel>
                  <Select
                    value={selectedInstitution}
                    onChange={(e) => {
                      setSelectedInstitution(e.target.value);
                      setSelectedCourse('');
                    }}
                    label="Select Institution"
                  >
                    {institutions.filter(inst => inst.status === 'approved').map(inst => (
                      <MenuItem key={inst.id} value={inst.id}>
                        {inst.name} - {inst.location}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <FormControl fullWidth disabled={!selectedInstitution}>
                  <InputLabel>Select Course</InputLabel>
                  <Select
                    value={selectedCourse}
                    onChange={(e) => setSelectedCourse(e.target.value)}
                    label="Select Course"
                  >
                    {selectedInstitution && institutions
                      .find(inst => inst.id === parseInt(selectedInstitution))
                      ?.faculties.flatMap(f => f.courses)
                      .map(course => (
                        <MenuItem key={course.id} value={course.id}>
                          {course.name} - {course.duration}
                        </MenuItem>
                      ))
                    }
                  </Select>
                </FormControl>

                <Button
                  variant="contained"
                  size="large"
                  onClick={handleApplication}
                  disabled={!selectedInstitution || !selectedCourse}
                  startIcon={<Add />}
                >
                  Submit Application
                </Button>
              </Box>
            </Card>
          </Grid>

          <Grid item xs={12} md={6}>
            <Card sx={{ p: 3 }}>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                Available Institutions
              </Typography>
              <List>
                {institutions.filter(inst => inst.status === 'approved').map(inst => (
                  <ListItem key={inst.id}>
                    <ListItemIcon>
                      <Business color="primary" />
                    </ListItemIcon>
                    <ListItemText
                      primary={inst.name}
                      secondary={`${inst.type}  ${inst.location}  ${inst.faculties.flatMap(f => f.courses).length} courses`}
                    />
                  </ListItem>
                ))}
              </List>
            </Card>
          </Grid>
        </Grid>
      )}

      {activeTab === 1 && (
        <Card sx={{ p: 3 }}>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
            My Applications
          </Typography>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Institution</TableCell>
                  <TableCell>Course</TableCell>
                  <TableCell>Faculty</TableCell>
                  <TableCell>Applied Date</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {studentApplications.map(app => (
                  <TableRow key={app.id}>
                    <TableCell>{app.institutionName}</TableCell>
                    <TableCell>{app.courseName}</TableCell>
                    <TableCell>{app.faculty}</TableCell>
                    <TableCell>{app.appliedDate}</TableCell>
                    <TableCell>
                      <Chip 
                        label={app.status} 
                        color={
                          app.status === 'admitted' ? 'success' : 
                          app.status === 'rejected' ? 'error' : 'warning'
                        }
                      />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Card>
      )}

      {activeTab === 2 && (
        <Grid container spacing={3}>
          {availableJobs.map(job => (
            <Grid item xs={12} md={6} key={job.id}>
              <Card sx={{ p: 3, height: '100%' }}>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                  {job.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {job.company}  {job.location}
                </Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
