const InstitutionLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>For Institutions</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      Manage your academic programs and connect with students
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/institution/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Institution Login
      </Button>
    </Box>
    <Box sx={{ mt: 6, display: 'flex', gap: 4, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Course Management</Typography>
        <Typography variant="body2" color="text.secondary">
          Create and manage academic programs and courses
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Student Applications</Typography>
        <Typography variant="body2" color="text.secondary">
          Review and process student applications efficiently
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Analytics</Typography>
        <Typography variant="body2" color="text.secondary">
          Track application trends and program performance
        </Typography>
      </Box>
    </Box>
  </Container>
);

const CompanyLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>For Companies</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      Find talented graduates and manage recruitment
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/company/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Company Login
      </Button>
    </Box>
    <Box sx={{ mt: 6, display: 'flex', gap: 4, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Post Jobs</Typography>
        <Typography variant="body2" color="text.secondary">
          Create and publish job opportunities for graduates
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Find Talent</Typography>
        <Typography variant="body2" color="text.secondary">
          Connect with qualified candidates from top institutions
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Manage Applications</Typography>
        <Typography variant="body2" color="text.secondary">
          Review and process job applications efficiently
        </Typography>
      </Box>
    </Box>
  </Container>
);

const AdminLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>Administration</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      System administration and management
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/admin/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Admin Login
      </Button>
    </Box>
    <Box sx={{ mt: 6, display: 'flex', gap: 4, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>User Management</Typography>
        <Typography variant="body2" color="text.secondary">
          Manage all users, institutions, and companies in the system
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>System Analytics</Typography>
        <Typography variant="body2" color="text.secondary">
          Monitor system performance and user activities
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Reports</Typography>
        <Typography variant="body2" color="text.secondary">
          Generate comprehensive reports and insights
        </Typography>
      </Box>
    </Box>
  </Container>
);

// ===== MAIN APP COMPONENT =====

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleRegister = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  React.useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const theme = createTheme({
    palette: {
      primary: {
        main: '#2196f3',
      },
      secondary: {
        main: '#ff4081',
      },
      background: {
        default: '#f5f5f5',
      },
    },
    typography: {
      fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    },
  });

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
          <Navbar user={user} onLogout={handleLogout} />
          <Box component="main" sx={{ flexGrow: 1 }}>
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<StudentLanding />} />
              <Route path="/student" element={<StudentLanding />} />
              <Route path="/institution" element={<InstitutionLanding />} />
              <Route path="/company" element={<CompanyLanding />} />
              <Route path="/admin" element={<AdminLanding />} />

              {/* Auth Routes */}
              <Route path="/student/login" element={<StudentLogin onLogin={handleLogin} />} />
              <Route path="/student/register" element={<StudentRegister onRegister={handleRegister} />} />
              <Route path="/institution/login" element={<InstitutionLogin onLogin={handleLogin} />} />
              <Route path="/company/login" element={<CompanyLogin onLogin={handleLogin} />} />
              <Route path="/admin/login" element={<AdminLogin onLogin={handleLogin} />} />

              {/* Public Features */}
              <Route path="/courses" element={<BrowseCourses />} />
              <Route path="/jobs" element={<BrowseJobs />} />
              <Route path="/apply-course/:id" element={<ApplyCourse />} />
              <Route path="/jobs/:id" element={<ViewJob />} />

              {/* Protected Student Routes */}
              <Route 
                path="/student/dashboard" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['student']}>
                    <StudentDashboard user={user} />
                  </ProtectedRoute>
                } 
              />

              {/* Protected Institution Routes */}
              <Route 
                path="/institute/dashboard" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['institution']}>
                    <ManageApplications />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/institute/courses" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['institution']}>
                    <ManageCourse />
                  </ProtectedRoute>
                } 
              />

              {/* Protected Company Routes */}
              <Route 
                path="/company/dashboard" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['company']}>
                    <PostJob />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/company/applicants" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['company']}>
                    <ViewApplicants />
                  </ProtectedRoute>
                } 
              />

              {/* Protected Admin Routes */}
              <Route 
                path="/admin/dashboard" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['admin']}>
                    <Reports />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/admin/companies" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['admin']}>
                    <ManageCompanies />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/admin/institutions" 
                element={
                  <ProtectedRoute user={user} allowedRoles={['admin']}>
                    <ManageInstitution />
                  </ProtectedRoute>
                } 
              />

              {/* 404 Route */}
              <Route path="*" element={
                <Container sx={{ py: 8, textAlign: 'center' }}>
                  <Typography variant="h3" gutterBottom>404</Typography>
                  <Typography variant="h6" gutterBottom>Page Not Found</Typography>
                  <Button variant="contained" component={Link} to="/" sx={{ mt: 2 }}>
                    Go Home
                  </Button>
                </Container>
              } />
            </Routes>
          </Box>
          <Footer />
        </Box>
      </Router>
    </ThemeProvider>
  );
};

export default App;
