import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container,
  Typography,
  Button,
  Paper,
  TextField,
  Alert,
  CircularProgress,
  Stepper,
  Step,
  StepLabel
} from '@mui/material';
import { Link } from 'react-router-dom';
import {
  School,
  Business,
  BusinessCenter,
  AdminPanelSettings,
  Login,
  HowToReg
} from '@mui/icons-material';

// Import available components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import ProtectedRoute from './components/ProtectedRoute';

// ===== LOGIN COMPONENTS =====

const StudentLogin = ({ onLogin }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await new Promise(resolve => setTimeout(resolve, 1500));

      if (formData.email === 'student@test.com' && formData.password === 'student123') {
        onLogin({
          id: 1,
          name: 'Test Student',
          email: formData.email,
          role: 'student',
        });
        navigate('/student/dashboard');
      } else {
        setError('Invalid credentials. Use student@test.com / student123');
      }
    } catch (err) {
      setError('Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', display: 'flex', alignItems: 'center' }}>
      <Container maxWidth="sm">
        <Paper elevation={24} sx={{ p: 4, borderRadius: 3 }}>
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <School sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
            <Typography variant="h4" fontWeight="bold" gutterBottom>Student Login</Typography>
          </Box>

          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

          <Box component="form" onSubmit={handleSubmit}>
            <TextField fullWidth label="Email" type="email" margin="normal" required 
              value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} />
            <TextField fullWidth label="Password" type="password" margin="normal" required 
              value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} />

            <Button type="submit" fullWidth variant="contained" disabled={loading} sx={{ mt: 3, py: 1.5 }}
              startIcon={loading ? <CircularProgress size={20} /> : <Login />}>
              {loading ? 'Signing In...' : 'Sign In'}
            </Button>
          </Box>

          <Box sx={{ textAlign: 'center', mt: 2 }}>
            <Typography variant="body2" color="text.secondary">
              Demo: student@test.com / student123
            </Typography>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

const StudentRegister = ({ onRegister }) => {
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phone: ''
  });
  const navigate = useNavigate();
  const steps = ['Personal Information', 'Academic Details', 'Complete'];

  const handleSubmit = async () => {
    setLoading(true);
    try {
      await new Promise(resolve => setTimeout(resolve, 2000));
      onRegister({
        id: Math.random().toString(36).substr(2, 9),
        name: `${formData.firstName} ${formData.lastName}`,
        email: formData.email,
        role: 'student',
      });
      navigate('/student/dashboard');
    } catch (error) {
      console.error('Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <School sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
          <Typography variant="h4" fontWeight="bold">Student Registration</Typography>
        </Box>

        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}><StepLabel>{label}</StepLabel></Step>
          ))}
        </Stepper>

        {activeStep === 0 && (
          <Box>
            <TextField fullWidth label="First Name" margin="normal" required 
              value={formData.firstName} onChange={(e) => setFormData({...formData, firstName: e.target.value})} />
            <TextField fullWidth label="Last Name" margin="normal" required 
              value={formData.lastName} onChange={(e) => setFormData({...formData, lastName: e.target.value})} />
            <TextField fullWidth label="Email" type="email" margin="normal" required 
              value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} />
            <Button variant="contained" onClick={() => setActiveStep(1)} sx={{ mt: 2 }}>
              Next
            </Button>
          </Box>
        )}

        {activeStep === 1 && (
          <Box>
            <TextField fullWidth label="Password" type="password" margin="normal" required 
              value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} />
            <TextField fullWidth label="Phone" margin="normal" 
              value={formData.phone} onChange={(e) => setFormData({...formData, phone: e.target.value})} />
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              <Button onClick={() => setActiveStep(0)}>Back</Button>
              <Button variant="contained" onClick={() => setActiveStep(2)}>Next</Button>
            </Box>
          </Box>
        )}

        {activeStep === 2 && (
          <Box sx={{ textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>Ready to create your account?</Typography>
            <Button
              variant="contained"
              onClick={handleSubmit}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : <HowToReg />}
              sx={{ mt: 2 }}
            >
              {loading ? 'Creating Account...' : 'Complete Registration'}
            </Button>
          </Box>
        )}
      </Paper>
    </Container>
  );
};

