import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container,
  Typography,
  Button
} from '@mui/material';
import { Link } from 'react-router-dom';

// Import available components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import ProtectedRoute from './components/ProtectedRoute';

// Import actual components (you'll need to create these files)
import StudentLogin from './components/StudentLogin';
import StudentRegister from './components/StudentRegister';
import InstitutionLogin from './components/InstitutionLogin';
import CompanyLogin from './components/CompanyLogin';
import AdminLogin from './components/AdminLogin';

// Import other actual components
import StudentDashboard from './components/StudentDashboard';
import ApplyCourse from './components/ApplyCourse';
import ViewJob from './components/ViewJob';
import ManageApplications from './components/ManageApplications';
import ManageCourse from './components/ManageCourse';
import PostJob from './components/PostJob';
import ViewApplicants from './components/ViewApplicants';
import ManageCompanies from './components/ManageCompanies';
import ManageInstitution from './components/ManageInstitution';
import Reports from './components/Reports';

// Placeholder Components for missing pages
const Home = () => <PlaceholderPage title="Career Guidance Platform" description="Welcome to our platform - Your journey to academic and career success starts here!" />;
const InstituteRegister = () => <PlaceholderPage title="Institute Register" />;
const CompanyRegister = () => <PlaceholderPage title="Company Register" />;

// Student Landing Page - This is what shows when you go to "/student"
const StudentLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>For Students</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      Start your academic and career journey with us
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/student/register"
        sx={{ px: 4, py: 1.5 }}
      >
        Create Account
      </Button>
      <Button 
        variant="outlined" 
        size="large" 
        component={Link} 
        to="/student/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Sign In
      </Button>
    </Box>
    <Box sx={{ mt: 6, display: 'flex', gap: 4, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Find Courses</Typography>
        <Typography variant="body2" color="text.secondary">
          Discover courses from top institutions that match your interests
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Career Guidance</Typography>
        <Typography variant="body2" color="text.secondary">
          Get personalized career recommendations based on your skills
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Job Opportunities</Typography>
        <Typography variant="body2" color="text.secondary">
          Connect with companies and find your dream job
        </Typography>
      </Box>
    </Box>
  </Container>
);

// Institution Landing Page
const InstitutionLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>For Institutions</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      Manage your academic programs and connect with students
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/institute/register"
        sx={{ px: 4, py: 1.5 }}
      >
        Register Institution
      </Button>
      <Button 
        variant="outlined" 
        size="large" 
        component={Link} 
        to="/institute/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Institution Login
      </Button>
    </Box>
  </Container>
);

// Company Landing Page
const CompanyLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>For Companies</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      Find qualified candidates and post job opportunities
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/company/register"
        sx={{ px: 4, py: 1.5 }}
      >
        Register Company
      </Button>
      <Button 
        variant="outlined" 
        size="large" 
        component={Link} 
        to="/company/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Company Login
      </Button>
    </Box>
  </Container>
);

// Admin Landing Page - This is what shows when you go to "/admin"
const AdminLanding = () => (
  <Container sx={{ py: 8, textAlign: 'center' }}>
    <Typography variant="h3" gutterBottom>Admin Portal</Typography>
    <Typography variant="h6" color="text.secondary" gutterBottom>
      System administration and management
    </Typography>
    <Box sx={{ mt: 4, display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Button 
        variant="contained" 
        size="large" 
        component={Link} 
        to="/admin/login"
        sx={{ px: 4, py: 1.5 }}
      >
        Admin Login
      </Button>
    </Box>
    <Box sx={{ mt: 6, display: 'flex', gap: 4, justifyContent: 'center', flexWrap: 'wrap' }}>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Manage Institutions</Typography>
        <Typography variant="body2" color="text.secondary">
          Approve and manage educational institutions
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>Manage Companies</Typography>
        <Typography variant="body2" color="text.secondary">
          Approve and manage company accounts
        </Typography>
      </Box>
      <Box sx={{ textAlign: 'center', maxWidth: 300 }}>
        <Typography variant="h6" gutterBottom>System Reports</Typography>
        <Typography variant="body2" color="text.secondary">
          View platform analytics and reports
        </Typography>
      </Box>
    </Box>
  </Container>
);

// Placeholder Page Component
function PlaceholderPage({ title, description = "This page is coming soon...", showHomeButton = true }) {
  return (
    <Container sx={{ py: 8, textAlign: 'center' }}>
      <Typography variant="h3" gutterBottom>{title}</Typography>
      <Typography variant="h6" color="text.secondary" gutterBottom>
        {description}
      </Typography>
      {showHomeButton && (
        <Box sx={{ mt: 3 }}>
          <Button variant="contained" component={Link} to="/">
            Go Back Home
          </Button>
        </Box>
      )}
    </Container>
  );
}

// Create theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
      light: '#42a5f5',
      dark: '#1565c0',
    },
    secondary: {
      main: '#dc004e',
      light: '#ff5983',
      dark: '#9a0036',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 700,
    },
    h3: {
      fontWeight: 600,
    },
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          borderRadius: 8,
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

// Main App Component
function AppContent() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const location = useLocation();

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const handleRegister = (userData) => {
    setUser(userData);
  };

  // Check if current route is an auth page (to hide navbar/footer)
  const isAuthPage = [
    '/student/login',
    '/student/register', 
    '/institute/login',
    '/institute/register',
    '/company/login',
    '/company/register',
    '/admin/login'
  ].includes(location.pathname);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {!isAuthPage && <Navbar user={user} onLogout={handleLogout} />}

      <Box component="main" sx={{ flexGrow: 1 }}>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />

          {/* Landing Pages */}
          <Route path="/student" element={<StudentLanding />} />
          <Route path="/institute" element={<InstitutionLanding />} />
          <Route path="/company" element={<CompanyLanding />} />
          <Route path="/admin" element={<AdminLanding />} />

          {/* Authentication Routes */}
          <Route 
            path="/student/login" 
            element={<StudentLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/student/register" 
            element={<StudentRegister onRegister={handleRegister} />} 
          />
          <Route 
            path="/institute/login" 
            element={<InstitutionLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/institute/register" 
            element={<InstituteRegister />} 
          />
          <Route 
            path="/company/login" 
            element={<CompanyLogin onLogin={handleLogin} />} 
          />
          <Route 
            path="/company/register" 
            element={<CompanyRegister />} 
          />
          <Route 
            path="/admin/login" 
            element={<AdminLogin onLogin={handleLogin} />} 
          />

          {/* Student Protected Routes */}
          <Route 
            path="/student/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <StudentDashboard user={user} />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/apply-course/:courseId" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <ApplyCourse />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/jobs/:jobId" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <ViewJob />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/courses" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <PlaceholderPage title="Browse Courses" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/jobs" 
            element={
              <ProtectedRoute user={user} requiredRole="student">
                <PlaceholderPage title="Browse Jobs" />
              </ProtectedRoute>
            } 
          />

          {/* Institution Protected Routes */}
          <Route 
            path="/institute/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <PlaceholderPage title="Institute Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/institute/applications" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <ManageApplications />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/institute/courses" 
            element={
              <ProtectedRoute user={user} requiredRole="institution">
                <ManageCourse />
              </ProtectedRoute>
            } 
          />

          {/* Company Protected Routes */}
          <Route 
            path="/company/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <PlaceholderPage title="Company Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/company/post-job" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <PostJob />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/company/applicants" 
            element={
              <ProtectedRoute user={user} requiredRole="company">
                <ViewApplicants />
              </ProtectedRoute>
            } 
          />

          {/* Admin Protected Routes */}
          <Route 
            path="/admin/dashboard" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <PlaceholderPage title="Admin Dashboard" />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/companies" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <ManageCompanies />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/institutions" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <ManageInstitution />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/reports" 
            element={
              <ProtectedRoute user={user} requiredRole="admin">
                <Reports />
              </ProtectedRoute>
            } 
          />

          {/* 404 Page */}
          <Route path="*" element={
            <Container sx={{ textAlign: 'center', py: 8 }}>
              <Typography variant="h3" gutterBottom>404 - Page Not Found</Typography>
              <Typography variant="h6" color="text.secondary" gutterBottom>
                The page you're looking for doesn't exist.
              </Typography>
              <Button variant="contained" component={Link} to="/" sx={{ mt: 2 }}>
                Go Back Home
              </Button>
            </Container>
          } />
        </Routes>
      </Box>

      {!isAuthPage && <Footer />}
    </Box>
  );
}

// Main App with Theme Provider
function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <AppContent />
      </Router>
    </ThemeProvider>
  );
}

export default App;
