import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  useTheme,
  useMediaQuery,
  CssBaseline
} from '@mui/material';
import {
  Menu as MenuIcon,
  School,
  BusinessCenter,
  Dashboard,
  Person,
  Group
} from '@mui/icons-material';

// Placeholder Components
function Home() {
  return (
    <Container sx={{ py: 8, textAlign: 'center' }}>
      <Typography variant="h3">Home</Typography>
      <Typography>Welcome to the Career Guidance Platform</Typography>
    </Container>
  );
}

function StudentRegister() {
  return <PlaceholderPage title="Student Register" />;
}
function StudentLogin() {
  return <PlaceholderPage title="Student Login" />;
}
function InstituteRegister() {
  return <PlaceholderPage title="Institute Register" />;
}
function InstituteLogin() {
  return <PlaceholderPage title="Institute Login" />;
}
function CompanyRegister() {
  return <PlaceholderPage title="Company Register" />;
}
function CompanyLogin() {
  return <PlaceholderPage title="Company Login" />;
}
function AdminLogin() {
  return <PlaceholderPage title="Admin Login" />;
}

function PlaceholderPage({ title }) {
  return (
    <Container sx={{ py: 8, textAlign: 'center' }}>
      <Typography variant="h3" gutterBottom>{title}</Typography>
      <Typography variant="h6" color="text.secondary">This page is coming soon...</Typography>
    </Container>
  );
}

// Navigation Component
function Navigation() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const location = useLocation();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const menuItems = [
    { text: 'Home', path: '/', icon: <Dashboard /> },
    { text: 'For Students', path: '/student', icon: <School />, children: [
      { text: 'Register', path: '/student/register' },
      { text: 'Login', path: '/student/login' }
    ]},
    { text: 'For Institutions', path: '/institute', icon: <Person />, children: [
      { text: 'Register', path: '/institute/register' },
      { text: 'Login', path: '/institute/login' }
    ]},
    { text: 'For Companies', path: '/company', icon: <BusinessCenter />, children: [
      { text: 'Register', path: '/company/register' },
      { text: 'Login', path: '/company/login' }
    ]},
    { text: 'Admin', path: '/admin/login', icon: <Group /> }
  ];

  const handleDrawerToggle = () => setMobileOpen(!mobileOpen);

  const drawer = (
    <Box sx={{ width: 250 }} onClick={handleDrawerToggle}>
      <Toolbar>
        <Typography variant="h6" color="primary" fontWeight="bold">CareerGuide</Typography>
      </Toolbar>
      <List>
        {menuItems.map((item) => (
          <ListItem
            key={item.text}
            component={Link}
            to={item.path}
            sx={{
              backgroundColor: location.pathname === item.path ? theme.palette.primary.main : 'transparent',
              color: location.pathname === item.path ? 'white' : 'inherit',
              '&:hover': { backgroundColor: theme.palette.primary.light, color: 'white' }
            }}
          >
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <AppBar
        position="static"
        elevation={2}
        sx={{
          background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`
        }}
      >
        <Toolbar>
          <IconButton color="inherit" aria-label="menu" edge="start" onClick={handleDrawerToggle} sx={{ mr: 2, display: { md: 'none' } }}>
            <MenuIcon />
          </IconButton>
          <School sx={{ mr: 2 }} />
          <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 'bold', fontSize: { xs: '1.1rem', md: '1.25rem' } }}>
            Career Guidance Platform
          </Typography>
          {!isMobile && (
            <Box sx={{ display: 'flex', gap: 1 }}>
              {menuItems.map((item) => (
                <Button
                  key={item.text}
                  color="inherit"
                  component={Link}
                  to={item.path}
                  startIcon={item.icon}
                  sx={{
                    fontWeight: location.pathname === item.path ? 'bold' : 'normal',
                    borderBottom: location.pathname === item.path ? '2px solid white' : 'none',
                    borderRadius: 0
                  }}
                >
                  {item.text}
                </Button>
              ))}
            </Box>
          )}
        </Toolbar>
      </AppBar>

      <Drawer
        variant="temporary"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{ keepMounted: true }}
        sx={{
          display: { xs: 'block', md: 'none' },
          '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 250 }
        }}
      >
        {drawer}
      </Drawer>
    </>
  );
}

// Main App
function App() {
  return (
    <Router>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        <Navigation />
        <Box component="main" sx={{ flexGrow: 1 }}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/student/register" element={<StudentRegister />} />
            <Route path="/student/login" element={<StudentLogin />} />
            <Route path="/institute/register" element={<InstituteRegister />} />
            <Route path="/institute/login" element={<InstituteLogin />} />
