import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Box,
  Container,
  Typography,
  Button,
  Paper,
  TextField,
  Alert,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  AppBar,
  Toolbar,
  IconButton,
  Avatar,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Tabs,
  Tab,
  Stepper,
  Step,
  StepLabel,
  Snackbar,
  CardActions,
  CardHeader,
  Divider,
  Tooltip,
  Fab
} from '@mui/material';
import {
  School,
  Business,
  BusinessCenter,
  AdminPanelSettings,
  Person,
  ExitToApp,
  Dashboard as DashboardIcon,
  People,
  Work,
  Assignment,
  CheckCircle,
  Warning,
  TrendingUp,
  Email,
  Phone,
  LocationOn,
  Add,
  Edit,
  Delete,
  Visibility,
  Upload,
  Download,
  Notifications,
  DateRange,
  Book,
  EmojiEvents,
  Security,
  Star,
  Search,
  FilterList,
  Refresh,
  MoreVert
} from '@mui/icons-material';

// ===== THEME CONFIGURATION =====
const theme = createTheme({
  palette: {
    primary: {
      main: '#2E7D32',
      light: '#4CAF50',
      dark: '#1B5E20'
    },
    secondary: {
      main: '#FF6F00',
      light: '#FF9800',
      dark: '#E65100'
    },
    background: {
      default: '#f8f9fa',
      paper: '#ffffff'
    }
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h3: {
      fontWeight: 700,
    },
    h4: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    }
  },
  shape: {
    borderRadius: 12
  }
});

// ===== DATA STORAGE & CONTEXT =====
const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Initialize data
  const [institutions, setInstitutions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [students, setStudents] = useState([]);
  const [applications, setApplications] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [jobApplications, setJobApplications] = useState([]);

  useEffect(() => {
    initializeData();
    const savedUser = localStorage.getItem('currentUser');
    if (savedUser) {
      setCurrentUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  const initializeData = () => {
    // Sample institutions
    const sampleInstitutions = [
      {
        id: 1,
        name: 'Limkokwing University of Creative Technology',
        email: 'admin@limkokwing.ls',
        type: 'University',
        location: 'Maseru',
        phone: '+266 2231 7242',
        status: 'approved',
        faculties: [
          {
            id: 1,
            name: 'Faculty of Information & Communication Technology',
            courses: [
              { id: 1, name: 'BSc in Information Technology', duration: '4 years', requirements: 'LGCSE with Math and English' },
              { id: 2, name: 'BSc in Software Engineering', duration: '4 years', requirements: 'LGCSE with Math and Physics' }
            ]
          },
          {
            id: 2,
            name: 'Faculty of Business',
            courses: [
              { id: 3, name: 'BBA in Business Management', duration: '3 years', requirements: 'LGCSE with Math and English' }
            ]
          }
        ]
      },
      {
        id: 2,
        name: 'National University of Lesotho',
        email: 'admin@nul.ls',
        type: 'University',
        location: 'Roma',
        phone: '+266 2234 0601',
        status: 'approved',
        faculties: [
          {
            id: 3,
            name: 'Faculty of Science & Technology',
            courses: [
              { id: 4, name: 'BSc in Computer Science', duration: '4 years', requirements: 'LGCSE with Math and Science' }
            ]
          }
        ]
      }
    ];

    // Sample companies
    const sampleCompanies = [
      {
        id: 1,
        name: 'Econet Telecom Lesotho',
        email: 'hr@econet.ls',
        industry: 'Telecommunications',
        location: 'Maseru',
        phone: '+266 2232 0000',
        status: 'approved'
      },
      {
        id: 2,
        name: 'Standard Bank Lesotho',
        email: 'careers@standardbank.ls',
        industry: 'Banking',
        location: 'Maseru',
        phone: '+266 2231 0001',
        status: 'approved'
      }
    ];

    // Sample students
    const sampleStudents = [
      {
        id: 1,
        name: 'John Molapo',
        email: 'john@student.ls',
        phone: '+266 5954 1234',
        location: 'Maseru',
        qualifications: {
          lgcse: {
            english: 'A',
            math: 'B',
            science: 'B',
            sesotho: 'A'
          }
        },
        status: 'active'
      }
    ];

    // Sample jobs
    const sampleJobs = [
      {
        id: 1,
        title: 'Junior Software Developer',
        company: 'Econet Telecom Lesotho',
        companyId: 1,
        location: 'Maseru',
        type: 'Full-time',
        salary: 'M8,000 - M12,000',
        description: 'We are looking for a passionate Junior Software Developer to design, develop and install software solutions.',
        requirements: 'BSc in Computer Science or related field, knowledge of programming languages, problem-solving skills',
        postedDate: '2024-01-15',
        status: 'active'
      },
      {
        id: 2,
        title: 'IT Support Specialist',
        company: 'Standard Bank Lesotho',
        companyId: 2,
        location: 'Maseru',
        type: 'Full-time',
        salary: 'M6,000 - M9,000',
        description: 'Provide technical assistance to our staff and customers, troubleshoot hardware and software issues.',
        requirements: 'Diploma in IT, knowledge of Windows OS, good communication skills',
        postedDate: '2024-01-10',
        status: 'active'
      }
    ];

    setInstitutions(sampleInstitutions);
    setCompanies(sampleCompanies);
    setStudents(sampleStudents);
    setJobs(sampleJobs);

    // Load from localStorage if available
    const savedInstitutions = localStorage.getItem('institutions');
    const savedCompanies = localStorage.getItem('companies');
    const savedStudents = localStorage.getItem('students');
    const savedApplications = localStorage.getItem('applications');
    const savedJobs = localStorage.getItem('jobs');
    const savedJobApplications = localStorage.getItem('jobApplications');

    if (savedInstitutions) setInstitutions(JSON.parse(savedInstitutions));
    if (savedCompanies) setCompanies(JSON.parse(savedCompanies));
    if (savedStudents) setStudents(JSON.parse(savedStudents));
    if (savedApplications) setApplications(JSON.parse(savedApplications));
    if (savedJobs) setJobs(JSON.parse(savedJobs));
    if (savedJobApplications) setJobApplications(JSON.parse(savedJobApplications));
  };

  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('institutions', JSON.stringify(institutions));
  }, [institutions]);

  useEffect(() => {
    localStorage.setItem('companies', JSON.stringify(companies));
  }, [companies]);

  useEffect(() => {
    localStorage.setItem('students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('applications', JSON.stringify(applications));
  }, [applications]);

  useEffect(() => {
    localStorage.setItem('jobs', JSON.stringify(jobs));
  }, [jobs]);

  useEffect(() => {
    localStorage.setItem('jobApplications', JSON.stringify(jobApplications));
  }, [jobApplications]);

  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const login = (email, password) => {
    // Demo login - in real app, this would be an API call
    const demoUsers = {
      'admin@lesotho.ls': { 
        id: 1, 
        name: 'System Administrator', 
        email: 'admin@lesotho.ls', 
        password: 'admin123', 
        role: 'admin' 
      },
      'institution@limkokwing.ls': { 
        id: 2, 
        name: 'Limkokwing University', 
        email: 'institution@limkokwing.ls', 
        password: 'inst123', 
        role: 'institution',
        institutionId: 1
      },
      'company@econet.ls': { 
        id: 3, 
        name: 'Econet Telecom', 
        email: 'company@econet.ls', 
        password: 'company123', 
        role: 'company',
        companyId: 1
      },
      'student@test.ls': { 
        id: 4, 
        name: 'Test Student', 
        email: 'student@test.ls', 
        password: 'student123', 
        role: 'student',
        studentId: 1
      }
    };

    const user = demoUsers[email];
    if (user && user.password === password) {
      setCurrentUser(user);
      localStorage.setItem('currentUser', JSON.stringify(user));
      showSnackbar(`Welcome back, ${user.name}!`, 'success');
      return { success: true, user };
    }
    return { success: false, error: 'Invalid email or password' };
  };

  const register = (userData) => {
    const newUser = {
      id: Date.now(),
      ...userData,
      status: userData.role === 'student' ? 'active' : 'pending'
    };

    if (userData.role === 'student') {
      const newStudent = {
        id: Date.now(),
        name: userData.name,
        email: userData.email,
        phone: userData.phone,
        location: userData.location,
        qualifications: {},
        status: 'active'
      };
      setStudents(prev => [...prev, newStudent]);
      newUser.studentId = newStudent.id;
    } else if (userData.role === 'institution') {
      const newInstitution = {
        id: Date.now(),
        name: userData.name,
        email: userData.email,
        type: userData.institutionType,
        location: userData.location,
        phone: userData.phone,
        status: 'pending',
        faculties: []
      };
      setInstitutions(prev => [...prev, newInstitution]);
      newUser.institutionId = newInstitution.id;
    } else if (userData.role === 'company') {
      const newCompany = {
        id: Date.now(),
        name: userData.name,
        email: userData.email,
        industry: userData.industry,
        location: userData.location,
        phone: userData.phone,
        status: 'pending'
      };
      setCompanies(prev => [...prev, newCompany]);
      newUser.companyId = newCompany.id;
    }

    setCurrentUser(newUser);
    localStorage.setItem('currentUser', JSON.stringify(newUser));
    showSnackbar('Registration successful!', 'success');
    return { success: true, user: newUser };
  };

  const logout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    showSnackbar('Logged out successfully', 'info');
  };

  const addInstitution = (institutionData) => {
    const newInstitution = {
      id: Date.now(),
      ...institutionData,
      status: 'approved',
      faculties: []
    };
    setInstitutions(prev => [...prev, newInstitution]);
    showSnackbar('Institution added successfully!', 'success');
    return newInstitution;
  };

  const addFaculty = (institutionId, facultyData) => {
    setInstitutions(prev => prev.map(inst => 
      inst.id === institutionId 
        ? { ...inst, faculties: [...inst.faculties, { id: Date.now(), ...facultyData, courses: [] }] }
        : inst
    ));
    showSnackbar('Faculty added successfully!', 'success');
  };

  const addCourse = (institutionId, facultyId, courseData) => {
    setInstitutions(prev => prev.map(inst => 
      inst.id === institutionId 
        ? {
            ...inst,
            faculties: inst.faculties.map(faculty =>
              faculty.id === facultyId
                ? { ...faculty, courses: [...faculty.courses, { id: Date.now(), ...courseData }] }
                : faculty
            )
          }
        : inst
    ));
    showSnackbar('Course added successfully!', 'success');
  };

  const submitApplication = (applicationData) => {
    const institutionApplications = applications.filter(app => 
      app.studentId === applicationData.studentId && app.institutionId === applicationData.institutionId
    );

    if (institutionApplications.length >= 2) {
      showSnackbar('You can only apply for maximum 2 courses per institution', 'error');
      return { success: false };
    }

    const newApplication = {
      id: Date.now(),
      ...applicationData,
      status: 'pending',
      appliedDate: new Date().toISOString().split('T')[0]
    };
    setApplications(prev => [...prev, newApplication]);
    showSnackbar('Application submitted successfully!', 'success');
    return { success: true };
  };

  const updateApplicationStatus = (applicationId, status) => {
    setApplications(prev => prev.map(app =>
      app.id === applicationId ? { ...app, status } : app
    ));
    showSnackbar(`Application ${status.toLowerCase()}!`, 'success');
  };

  const postJob = (jobData) => {
    const newJob = {
      id: Date.now(),
      ...jobData,
      postedDate: new Date().toISOString().split('T')[0],
      status: 'active'
    };
    setJobs(prev => [...prev, newJob]);
    showSnackbar('Job posted successfully!', 'success');
    return newJob;
  };

  const applyForJob = (jobApplicationData) => {
    const newJobApplication = {
      id: Date.now(),
      ...jobApplicationData,
      appliedDate: new Date().toISOString().split('T')[0],
      status: 'pending'
    };
    setJobApplications(prev => [...prev, newJobApplication]);
    showSnackbar('Job application submitted!', 'success');
    return { success: true };
  };

  const deleteInstitution = (institutionId) => {
    setInstitutions(prev => prev.filter(inst => inst.id !== institutionId));
    showSnackbar('Institution deleted successfully!', 'success');
  };

  const deleteCompany = (companyId) => {
    setCompanies(prev => prev.filter(comp => comp.id !== companyId));
    showSnackbar('Company deleted successfully!', 'success');
  };

  const deleteCourse = (institutionId, facultyId, courseId) => {
    setInstitutions(prev => prev.map(inst => 
      inst.id === institutionId 
        ? {
            ...inst,
            faculties: inst.faculties.map(faculty =>
              faculty.id === facultyId
                ? { ...faculty, courses: faculty.courses.filter(course => course.id !== courseId) }
                : faculty
            )
          }
        : inst
    ));
    showSnackbar('Course deleted successfully!', 'success');
  };

  const value = {
    currentUser,
    institutions,
    companies,
    students,
    applications,
    jobs,
    jobApplications,
    login,
    register,
    logout,
    addInstitution,
    addFaculty,
    addCourse,
    submitApplication,
    updateApplicationStatus,
    postJob,
    applyForJob,
    deleteInstitution,
    deleteCompany,
    deleteCourse,
    showSnackbar,
    setInstitutions,
    setCompanies,
    setStudents
  };

  return (
    <AppContext.Provider value={value}>
      {children}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={4000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        message={snackbar.message}
      />
    </AppContext.Provider>
  );
};

const useApp = () => {
  const context = React.useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
};

